export async function test_get_user_by_id_with_valid_and_invalid_id(connection: api.IConnection): Promise<void> {
  // 1. Create a new user to obtain a valid user ID
  const createInput: IUser.ICreate = {
    // Use typia.random for generating required fields
    organization_id: typia.random<string & tags.Format<"uuid">>(),
    role_id: typia.random<string & tags.Format<"uuid">>(),
    username: `e2e_test_${Math.random().toString(36).slice(2, 10)}`,
    email: `e2e_test_${Math.random().toString(36).slice(2, 10)}@test.com`,
    password: "TestPassword!123"
    // phone is optional and omitted
  };
  const created: IUser = await api.functional.core.users.post(connection, { body: createInput });
  typia.assert<IUser>(created);
  TestValidator.equals("Returned user should match input (except sensitive fields)")(
    // Only compare overlappable fields
    {
      username: createInput.username,
      email: createInput.email,
      role_id: createInput.role_id
    }
  )({
    username: created.username,
    email: created.email,
    role_id: created.role_id
  });

