    await api.functional.core.users.getById(connection, { id: invalidId });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";

export async function test_create_user_with_valid_invalid_and_duplicate_data(connection: api.IConnection): Promise<void> {
  // Helper UUIDs for organization/role
  const organization_id = "10000000-0000-4000-8000-000000000001";
  const second_organization_id = "10000000-0000-4000-8000-000000000002";
  const role_id = "20000000-0000-4000-8000-000000000001";
  const duplicate_email = `user_${Math.floor(Math.random()*1e6)}@test.com`;
  const duplicate_username = `user_${Math.floor(Math.random()*1e6)}`;

  // 1. Create a valid user (all fields)
  const validInput: IUser.ICreate = {
    organization_id,
    role_id,
    username: duplicate_username,
    email: duplicate_email as string & typia.tags.Format<"email">,
    phone: "+821012345678",
    password: "securePassword!1234",
  };

  const created = await api.functional.core.users.post(connection, { body: validInput });
  typia.assert<IUser>(created);

  // Validate essential output (password never returned)
  TestValidator.predicate("user has valid id")(!!created.id && typeof created.id === "string");
  TestValidator.equals("organization_id")(validInput.organization_id)(created.organization_id);
  TestValidator.equals("role_id")(validInput.role_id)(created.role_id);
  TestValidator.equals("username")(validInput.username)(created.username);
  TestValidator.equals("email")(validInput.email)(created.email);
  TestValidator.equals("phone")(validInput.phone)(created.phone);
  TestValidator.predicate("is active")(typeof created.is_active === "boolean");
  TestValidator.predicate("created_at")(!!created.created_at);
  TestValidator.predicate("updated_at")(!!created.updated_at);
  TestValidator.predicate("deleted_at null or string")(created.deleted_at === undefined || typeof created.deleted_at === "string");

  // 2. Try missing required fields
  const invalids: Partial<IUser.ICreate>[] = [
    { ...validInput, role_id: undefined as any },    // No role_id
    { ...validInput, username: undefined as any },   // No username
    { ...validInput, email: undefined as any },      // No email
    { ...validInput, password: undefined as any },   // No password
  ];

  for (const [i, input] of invalids.entries()) {
    await TestValidator.httpError(`missing required (${i})`)(400, 422)(
      () => api.functional.core.users.post(connection, { body: input as IUser.ICreate }),
    );
  }

  // 3. Duplicate email
  const dupEmailInput: IUser.ICreate = {
    ...validInput,
    username: `another_user_${Math.floor(Math.random()*1e6)}`,
  };
  await TestValidator.httpError("duplicate email")(400, 409, 422)(
    () => api.functional.core.users.post(connection, { body: dupEmailInput }),
  );

  // 4. Duplicate username
  const dupUsernameInput: IUser.ICreate = {
    ...validInput,
    email: `another_${Math.floor(Math.random()*1e6)}@test.com` as string & typia.tags.Format<"email">,
  };
  await TestValidator.httpError("duplicate username")(400, 409, 422)(
    () => api.functional.core.users.post(connection, { body: dupUsernameInput }),
  );

  // 5. Optional field null/omitted tests
  const inputNoOrgNoPhone: IUser.ICreate = {
    role_id,
    username: `nonorg_${Math.floor(Math.random()*1e6)}`,
    email: `nonorg_${Math.floor(Math.random()*1e6)}@test.com` as string & typia.tags.Format<"email">,
    password: "securePassword!4321",
  };
  const createdNoOrg = await api.functional.core.users.post(connection, { body: inputNoOrgNoPhone });
  typia.assert<IUser>(createdNoOrg);
  TestValidator.equals("org omitted")(
    inputNoOrgNoPhone.organization_id
  )(createdNoOrg.organization_id);
  TestValidator.equals("phone omitted")(
    inputNoOrgNoPhone.phone
  )(createdNoOrg.phone);
  TestValidator.equals("username omitted org/phone")(
    inputNoOrgNoPhone.username
  )(createdNoOrg.username);
}

