  // 1. Create a valid user
  const createInput: IUser.ICreate = {
    organization_id: typia.random<string & tags.Format<"uuid">>(),
    role_id: typia.random<string & tags.Format<"uuid">>(),
    username: "testuser_" + Math.random().toString(36).slice(-6),
    email: "testuser_" + Math.random().toString(36).slice(-6) + "@test.com",
    phone: null,
    password: "Passw0rd!123",
  };
  const user = await api.functional.core.users.post(connection, { body: createInput });
  typia.assert<IUser>(user);

  // 2. Valid update: Change email & username
  const updateInput: IUser.IUpdate = {
    email: "changed_" + Math.random().toString(36).slice(-6) + "@example.com",
    username: "updateduser_" + Math.random().toString(36).slice(-6),
  };
  const updated = await api.functional.core.users.putById(connection, {
