    full_name: "Alice Kim",
    avatar_url: "https://cdn.example.com/avatar/alice.png",
    timezone: "Asia/Seoul",
    bio: "Power user and localization QA",
    a11y_prefs: '{"dark_mode":true,"font_size":"large"}'
  };

  const created = await api.functional.core.userProfiles.post(connection, { body: profileInput });
  typia.assert<IUserProfile>(created);
  // Required fields
  TestValidator.predicate("returned id is uuid")(() => typeof created.id === "string" && /^[0-9a-fA-F-]{36}$/.test(created.id));
  // Editable fields match
  TestValidator.equals("user_id")<string>(profileInput.user_id)(created.user_id);
  TestValidator.equals("locale_id")(profileInput.locale_id)(created.locale_id);
  TestValidator.equals("full_name")(profileInput.full_name)(created.full_name);
  TestValidator.equals("avatar_url")(profileInput.avatar_url)(created.avatar_url);
  TestValidator.equals("timezone")(profileInput.timezone)(created.timezone);
  TestValidator.equals("bio")(profileInput.bio)(created.bio);
  TestValidator.equals("a11y_prefs")(profileInput.a11y_prefs)(created.a11y_prefs);
  // System fields
  TestValidator.predicate("created_at is iso date-time")(() => typeof created.created_at === "string" && created.created_at.includes("T"));
  TestValidator.predicate("updated_at is iso date-time")(() => typeof created.updated_at === "string" && created.updated_at.includes("T"));

  // 2. Negative: Missing required user_id
  const missingUserId: IUserProfile.ICreate = {
    // @ts-expect-error
    full_name: "Bob",
  };
  await TestValidator.httpError("missing required user_id")(400)(
    () => api.functional.core.userProfiles.post(connection, { body: missingUserId })
  );

  // 3. Negative: Malformed locale_id
  const badLocale: IUserProfile.ICreate = {
    user_id: "88888888-4444-aaaa-bbbb-cccccccccccc",
    locale_id: "NOT-A-UUID" as any,
    full_name: "Choi",
  };
  await TestValidator.httpError("bad locale_id")(400)(
    () => api.functional.core.userProfiles.post(connection, { body: badLocale })
  );

  // 4. Positive: Only required field supplied
  const minimum: IUserProfile.ICreate = {
    user_id: "ffffffff-5555-cccc-dddd-eeeeeeeeeeee",
  };
  const createdMin = await api.functional.core.userProfiles.post(connection, { body: minimum });
  typia.assert<IUserProfile>(createdMin);
  // Optionals should be null/undefined
  TestValidator.equals("locale_id empty")(null)(createdMin.locale_id ?? null);
  TestValidator.equals("full_name empty")(null)(createdMin.full_name ?? null);
  TestValidator.equals("avatar_url empty")(null)(createdMin.avatar_url ?? null);
  TestValidator.equals("timezone empty")(null)(createdMin.timezone ?? null);
  TestValidator.equals("bio empty")(null)(createdMin.bio ?? null);
  TestValidator.equals("a11y_prefs empty")(null)(createdMin.a11y_prefs ?? null);
  TestValidator.predicate("created_at is iso date-time")(() => typeof createdMin.created_at === "string" && createdMin.created_at.includes("T"));
  TestValidator.predicate("updated_at is iso date-time")(() => typeof createdMin.updated_at === "string" && createdMin.updated_at.includes("T"));

  // 5. Negative: Malformed user_id (not UUID)
  const badUserId: IUserProfile.ICreate = {
    user_id: "not-a-uuid" as any,
    full_name: "Kim Invalid",
  };
  await TestValidator.httpError("malformed user_id")(400)(
    () => api.functional.core.userProfiles.post(connection, { body: badUserId })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
