import { IUserProfile } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserProfile";

export async function test_update_user_profile_with_audit_and_error_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a user profile for update testing.
  const created: IUserProfile = await api.functional.core.userProfiles.post(connection, {
    body: {
      user_id: typia.random<string & tags.Format<"uuid">>(),
      full_name: "Original Name",
      avatar_url: "https://test.avatar/img1.png",
      timezone: "Asia/Seoul",
      bio: "Initial bio for audit.",
      a11y_prefs: '{"contrast":true}',
    },
  });
  typia.assert(created);

  // 2. Update some fields on the profile.
  const updateInput: IUserProfile.IUpdate = {
    full_name: "Updated Name",
    avatar_url: "https://test.avatar/img2.png",
    timezone: "UTC",
    bio: "Updated bio for audit logging.",
    a11y_prefs: '{"largeText":true}',
  };
  const updated: IUserProfile = await api.functional.core.userProfiles.putById(connection, {
    id: created.id,
    body: updateInput,
  });
  typia.assert(updated);
  // Validate returned fields and audit update
  TestValidator.equals("full_name should update")<string | undefined>(updateInput.full_name)(updated.full_name);
  TestValidator.equals("avatar_url should update")<string | undefined>(updateInput.avatar_url)(updated.avatar_url);
  TestValidator.equals("timezone should update")<string | undefined>(updateInput.timezone)(updated.timezone);
  TestValidator.equals("bio should update")<string | undefined>(updateInput.bio)(updated.bio);
  TestValidator.equals("a11y_prefs should update")<string | undefined>(updateInput.a11y_prefs)(updated.a11y_prefs);
  // updated_at should be newer than created_at
  TestValidator.predicate("updated_at should be newer")(() => new Date(updated.updated_at) > new Date(updated.created_at));

  // 3. Attempt update with an invalid field (simulate validation error i.e. empty update body)
  await TestValidator.httpError("empty update body should fail")(400)(async () => {
    await api.functional.core.userProfiles.putById(connection, {
      id: created.id,
      body: {}, // No updatable fields
    });
  });

  // 4. Attempt update of a non-existent profile (should return 404)
  await TestValidator.httpError("update non-existent id should 404")(404)(async () => {
    await api.functional.core.userProfiles.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(), // unlikely to exist
      body: { full_name: "Ghost Update" },
    });
  });

  // 5. (Optional) Attempt to modify immutable field user_id (should not be allowed)
  // Not possible, as user_id is not an updatable field per DTO.
}

