import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IOrganization } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrganization";
import { IPageIOrganization } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIOrganization";
import typia from "typia";

export async function test_list_organizations_with_pagination_and_filtering(connection: api.IConnection): Promise<void> {
  // 1. Set up known organizations
  const orgA: IOrganization = await api.functional.core.organizations.post(connection, {
    body: { name: "Acme Corp", code: "acme-corp" }
  });
  const orgB: IOrganization = await api.functional.core.organizations.post(connection, {
    body: { name: "Beta Group", code: "beta-group" }
  });
  const orgC: IOrganization = await api.functional.core.organizations.post(connection, {
    body: { name: "The Gamma", code: "the-gamma" }
  });

  // 2. Retrieve all organizations (default pagination)
  const page1: IPageIOrganization = await api.functional.core.organizations.patch(connection, {
    body: {}
  });
  typia.assert<IPageIOrganization>(page1);
  TestValidator.predicate("Contains created organizations")(page1.data.some(org => org.id === orgA.id && org.name === orgA.name));
  TestValidator.predicate("Contains created organizations")(page1.data.some(org => org.id === orgB.id && org.name === orgB.name));
  TestValidator.predicate("Contains created organizations")(page1.data.some(org => org.id === orgC.id && org.name === orgC.name));

  // 3. Retrieve organizations filtered by name
  const filteredByName: IPageIOrganization = await api.functional.core.organizations.patch(connection, {
    body: { name: "Acme Corp" }
  });
  typia.assert(filteredByName);
  TestValidator.equals("filtered contains Acme Corp")(
    [orgA]
  )(filteredByName.data);

  // 4. Retrieve a specific page with limit
  const limit1Page1: IPageIOrganization = await api.functional.core.organizations.patch(connection, {
    body: { page: 1, limit: 1 }
  });
  typia.assert(limit1Page1);
  TestValidator.equals("limit 1 page 1 has 1 record")(
    1
  )(limit1Page1.data.length);
  TestValidator.equals("pagination current is 1")(
    1
  )(limit1Page1.pagination.current);
  TestValidator.equals("pagination limit is 1")(
    1
  )(limit1Page1.pagination.limit);

  // 4-1: Page 2
  const limit1Page2: IPageIOrganization = await api.functional.core.organizations.patch(connection, {
    body: { page: 2, limit: 1 }
  });
  typia.assert(limit1Page2);
  TestValidator.equals("limit 1 page 2 has 1 record")(
    1
  )(limit1Page2.data.length);
  TestValidator.equals("pagination current is 2")(
    2
  )(limit1Page2.pagination.current);
  TestValidator.equals("pagination limit is 1")(
    1
  )(limit1Page2.pagination.limit);

  // 5. Invalid pagination/filter: negative page
  await TestValidator.httpError("Negative page should fail")(400, 422)(async () =>
    api.functional.core.organizations.patch(connection, {
      body: { page: -1 }
    })
  );
  // 5. Invalid pagination/filter: zero/negative limit
  await TestValidator.httpError("Zero limit should fail")(400, 422)(async () =>
    api.functional.core.organizations.patch(connection, {
      body: { limit: 0 }
    })
  );
  await TestValidator.httpError("Negative limit should fail")(400, 422)(async () =>
    api.functional.core.organizations.patch(connection, {
      body: { limit: -5 }
    })
  );

  // 5. Non-integer page/limit (use string deliberately)
  await TestValidator.httpError("Non-integer page")(400, 422)(async () =>
    api.functional.core.organizations.patch(connection, {
      body: { page: "abc" as any }
    })
  );
  await TestValidator.httpError("Non-integer limit")(400, 422)(async () =>
    api.functional.core.organizations.patch(connection, {
      body: { limit: "def" as any }
    })
  );
}

