/**
 * Test: Fetch organization details by ID and verify correctness, also test error handling for non-existent IDs.
 */
export async function test_get_organization_by_id_and_error_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create an organization to get a valid ID
  const createInput: IOrganization.ICreate = {
    name: `Org Test ${Date.now()}`,
    code: `testorg${Math.floor(Math.random() * 100000)}`,
  };
  const created: IOrganization = await api.functional.core.organizations.post(connection, {
    body: createInput,
  });
  typia.assert<IOrganization>(created);
  TestValidator.predicate("created org name matches")(created.name === createInput.name);
  TestValidator.predicate("created org code matches")(created.code === createInput.code);

  // 2. Fetch by ID with GET
  const found: IOrganization = await api.functional.core.organizations.getById(connection, {
    id: created.id,
  });
  typia.assert<IOrganization>(found);

  // 3. Verify all fields match exactly with what was created
  // Note: timestamps set by server, compare name, code, and id
  TestValidator.equals("id matches")(created.id)(found.id);
  TestValidator.equals("name matches")(created.name)(found.name);
  TestValidator.equals("code matches")(created.code)(found.code);
  // Timestamps: check ISO string, ensure created_at/updated_at are present and valid
  TestValidator.predicate("created_at set")(!!found.created_at && typeof found.created_at === "string");
  TestValidator.predicate("updated_at set")(!!found.updated_at && typeof found.updated_at === "string");

  // 4. Attempt to fetch with non-existent UUID (should error)
  const nonexistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("organization not found should error")(404)(async () => {
    await api.functional.core.organizations.getById(connection, { id: nonexistentId });
  });
}

