export async function test_create_organization_with_valid_invalid_and_duplicate(connection: api.IConnection): Promise<void> {
  // 1. Create an organization with valid required fields
  const orgInput: IOrganization.ICreate = {
    name: `Org_${Math.random().toString(36).substring(2, 10)}_${Date.now()}`,
    code: `org_code_${Math.random().toString(36).substring(2, 8)}_${Date.now()}`,
  };

  const organization: IOrganization = await api.functional.core.organizations.post(connection, { body: orgInput });
  typia.assert<IOrganization>(organization);
  TestValidator.equals("created organization name")<string>(orgInput.name)(organization.name);
  TestValidator.equals("created organization code")<string>(orgInput.code)(organization.code);
  TestValidator.predicate("organization id is a string")(() => typeof organization.id === "string" && organization.id.length > 0);
  TestValidator.predicate("organization created_at valid")(() => typeof organization.created_at === "string");
  TestValidator.predicate("organization updated_at valid")(() => typeof organization.updated_at === "string");

  // 2. Attempt creation missing 'name'
  const missingNameInput: Partial<IOrganization.ICreate> = {
    code: `unique_code_${Date.now()}`,
  };
  await TestValidator.httpError("missing name must fail")(400, 422)(
    () => api.functional.core.organizations.post(connection, { body: missingNameInput as IOrganization.ICreate })
  );

  // 3. Attempt creation missing 'code'
  const missingCodeInput: Partial<IOrganization.ICreate> = {
    name: `Another Org ${Date.now()}`,
  };
  await TestValidator.httpError("missing code must fail")(400, 422)(
    () => api.functional.core.organizations.post(connection, { body: missingCodeInput as IOrganization.ICreate })
  );

  // 4. Attempt duplicate (same name and code)
  const dupInput: IOrganization.ICreate = {
    name: orgInput.name,
    code: orgInput.code,
  };
  await TestValidator.httpError("duplicate organization must fail")(409, 422, 400)(
    () => api.functional.core.organizations.post(connection, { body: dupInput })
  );

  // 5. Attempt duplicate name but unique code
  const dupNameInput: IOrganization.ICreate = {
    name: orgInput.name,
    code: `uniq_code_${Date.now()}`,
  };
  await TestValidator.httpError("duplicate name must fail")(409, 422, 400)(
    () => api.functional.core.organizations.post(connection, { body: dupNameInput })
  );

  // 6. Attempt duplicate code but unique name
  const dupCodeInput: IOrganization.ICreate = {
    name: `Unique Org ${Date.now()}`,
    code: orgInput.code,
  };
  await TestValidator.httpError("duplicate code must fail")(409, 422, 400)(
    () => api.functional.core.organizations.post(connection, { body: dupCodeInput })
  );
