  orgA.name = "Role A1";
  orgA.code = "ROLE_A1";
  const roleA1 = await api.functional.core.roles.post(connection, { body: orgA });

  const orgB = { ...orgA, name: "Role B1", code: "ROLE_B1", organization_id: typia.random<IRole.ICreate>().organization_id };
  const roleB1 = await api.functional.core.roles.post(connection, { body: orgB });

  const orgA2 = { ...orgA, name: "Role A2", code: "ROLE_A2" };
  const roleA2 = await api.functional.core.roles.post(connection, { body: orgA2 });

  // 2.a. List all roles (should include all test roles)
  const all = await api.functional.core.roles.patch(connection, { body: {} });
  typia.assert<IPageIRole>(all);
  const roleIds = [roleA1.id, roleA2.id, roleB1.id];
  TestValidator.predicate("should include created roles")(() =>
    roleIds.every(id => all.data.some(x => x.id === id)));

  // 2.b. Filter by organization_id (orgA)
  const filteredA = await api.functional.core.roles.patch(connection, { body: { organization_id: orgA.organization_id } });
  typia.assert<IPageIRole>(filteredA);
  TestValidator.predicate("only orgA roles returned")(() =>
    filteredA.data.every(x => x.organization_id === orgA.organization_id)
  );

  // 2.c. Filter by name
  const byName = await api.functional.core.roles.patch(connection, { body: { name: orgA.name } });
  typia.assert<IPageIRole>(byName);
  TestValidator.predicate("role by name")(() =>
    byName.data.every(x => x.name === orgA.name)
  );

  // 2.d. Filter by code
  const byCode = await api.functional.core.roles.patch(connection, { body: { code: orgA.code } });
  typia.assert<IPageIRole>(byCode);
  TestValidator.predicate("role by code")(() =>
    byCode.data.length === 1 && byCode.data[0].id === roleA1.id
  );

  // 2.e. Pagination - limit = 1
  const paginated = await api.functional.core.roles.patch(connection, { body: { limit: 1 } });
  typia.assert<IPageIRole>(paginated);
  TestValidator.equals("pagination limit 1")(
    1
  )(paginated.data.length);
  TestValidator.predicate("valid pagination meta")(() =>
    typeof paginated.pagination.current === "number" &&
    paginated.pagination.limit === 1
  );

  // 3.a. Non-existent organization_id
  const bogusOrg = typia.random<IRole.ICreate>().organization_id;
  const noneOrg = await api.functional.core.roles.patch(connection, { body: { organization_id: bogusOrg } });
  typia.assert<IPageIRole>(noneOrg);
  TestValidator.equals("empty result for non-existent org")([] as IRole[])(noneOrg.data);

  // 3.b. Bogus name/code
  const noneName = await api.functional.core.roles.patch(connection, { body: { name: "@@@NO_SUCH_ROLE@@@" } });
  typia.assert<IPageIRole>(noneName);
  TestValidator.equals("empty result for bogus name")([] as IRole[])(noneName.data);
  const noneCode = await api.functional.core.roles.patch(connection, { body: { code: "@@@NO_SUCH_CODE@@@" } });
  typia.assert<IPageIRole>(noneCode);
  TestValidator.equals("empty result for bogus code")([] as IRole[])(noneCode.data);

  // 3.c. Invalid paginations
  await TestValidator.httpError("negative page")(400)(() =>
    api.functional.core.roles.patch(connection, { body: { page: -1 } })
