import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ISession } from "@ORGANIZATION/PROJECT-api/lib/structures/ISession";
import { IPageISession } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageISession";
import typia from "typia";

/**
 * Test session listing with pagination and filtering.
 */
export async function test_list_sessions_with_pagination_and_filtering(connection: api.IConnection): Promise<void> {
  // 1. Create session records for several users/devices
  const sessionA = await api.functional.core.sessions.post(connection, {
    body: {
      user_id: typia.random<`${string & typia.tags.Format<"uuid">}`>(),
      token: `token-A-${Math.random()}`,
      expires_at: new Date(Date.now() + 1e6).toISOString(),
      device_fingerprint: "devA",
      ip: "127.0.0.1",
    },
  });
  const sessionB = await api.functional.core.sessions.post(connection, {
    body: {
      user_id: typia.random<`${string & typia.tags.Format<"uuid">}`>(),
      token: `token-B-${Math.random()}`,
      expires_at: new Date(Date.now() + 2e6).toISOString(),
      device_fingerprint: "devB",
      ip: "10.0.0.1",
    },
  });
  const sessionC = await api.functional.core.sessions.post(connection, {
    body: {
      user_id: sessionA.user_id,
      token: `token-C-${Math.random()}`,
      expires_at: new Date(Date.now() + 3e6).toISOString(),
      device_fingerprint: "devA",
      ip: "172.16.0.1",
    },
  });

  // 2. List all sessions (default params)
  const listAll: IPageISession = await api.functional.core.sessions.patch(connection, {
    body: {},
  });
  typia.assert(listAll);
  TestValidator.predicate("Sessions should be non-empty")(() => listAll.data.length > 0);

  // Ensure all created sessions appear in the result (by id)
  const allSessionIds = listAll.data.map((x) => x.id);
  TestValidator.predicate("SessionA present")(allSessionIds.includes(sessionA.id));
  TestValidator.predicate("SessionB present")(allSessionIds.includes(sessionB.id));
  TestValidator.predicate("SessionC present")(allSessionIds.includes(sessionC.id));

  // 3. List with pagination (limit=2)
  const paginated: IPageISession = await api.functional.core.sessions.patch(connection, {
    body: { limit: 2 },
  });
  typia.assert(paginated);
  TestValidator.equals("Paginated limit", undefined)(paginated.data.length)(2);
  TestValidator.equals("Pagination.limit matches")(paginated.pagination.limit)(2);

  // 4. List with user_id filter (should match sessionA and sessionC)
  const filteredByUser: IPageISession = await api.functional.core.sessions.patch(connection, {
    body: { user_id: sessionA.user_id },
  });
  typia.assert(filteredByUser);
  TestValidator.predicate("Filtered data not empty")(() => filteredByUser.data.length >= 1);
  for (const s of filteredByUser.data) {
    TestValidator.equals("user_id filtered match")(s.user_id)(sessionA.user_id);
  }
  // Now filter by device_fingerprint (for devB, only sessionB)
  const filteredByDevice: IPageISession = await api.functional.core.sessions.patch(connection, {
    body: { device_fingerprint: "devB" },
  });
  typia.assert(filteredByDevice);
  TestValidator.equals("Device filter count")(filteredByDevice.data.length)(1);
  TestValidator.equals("Device filtered correct")(filteredByDevice.data[0].id)(sessionB.id);

  // 5. Invalid filter: negative page
  await TestValidator.httpError("Negative page triggers error")(400)(async () =>
    api.functional.core.sessions.patch(connection, {
      body: { page: -1 },
    })
  );

  // 6. Invalid filter: malformed user_id
  await TestValidator.httpError("Malformed user_id triggers error")(400)(async () =>
    api.functional.core.sessions.patch(connection, {
      body: { user_id: "not-a-uuid" as any },
    })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ISession } from "@ORGANIZATION/PROJECT-api/lib/structures/ISession";
import typia, { tags } from "typia";

export async function test_get_session_by_id_and_error_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new session, record its returned data and ID
  const sessionInput: ISession.ICreate = {
    user_id: typia.random<string & tags.Format<"uuid">>(),
    token: `test-token-${Math.random().toString(36).substring(2)}`,
    expires_at: new Date(Date.now() + 3600 * 1000).toISOString(), // 1 hour later
    device_fingerprint: null,
    ip: "192.0.2.1",
  };
  const created: ISession = await api.functional.core.sessions.post(connection, {
    body: sessionInput,
  });
  typia.assert<ISession>(created);
  TestValidator.equals("session fields match")(sessionInput.user_id)(created.user_id);
  TestValidator.equals("session fields match")(sessionInput.token)(created.token);
  TestValidator.equals("session fields match")(sessionInput.expires_at)(created.expires_at);
  TestValidator.equals("session fields match")(sessionInput.device_fingerprint)(created.device_fingerprint);
  TestValidator.equals("session fields match")(sessionInput.ip)(created.ip);
  // Check for returned id and created_at fields
  TestValidator.predicate("id present")(() => typeof created.id === "string" && !!created.id.length);
  TestValidator.predicate("created_at present")(() => typeof created.created_at === "string" && !!created.created_at.length);
  // revoked_at should be null or undefined at creation
  TestValidator.equals("revoked_at null at creation")(null)(created.revoked_at ?? null);

  // 2. Fetch the session by its ID and check all fields
  const fetched: ISession = await api.functional.core.sessions.getById(connection, { id: created.id });
  typia.assert<ISession>(fetched);
  TestValidator.equals("fetched session strictly equals created", (k) => k === "created_at" || k === "id" || k === "revoked_at")(
    { ...created, created_at: undefined, id: undefined, revoked_at: undefined },
  )(
    { ...fetched, created_at: undefined, id: undefined, revoked_at: undefined }
  );
  // Shallow equals on ID, created_at
  TestValidator.equals("id match")(created.id)(fetched.id);
  TestValidator.equals("created_at match")(created.created_at)(fetched.created_at);
  TestValidator.equals("revoked_at still null")(created.revoked_at ?? null)(fetched.revoked_at ?? null);

  // 3. Negative: Try fetching a non-existent session ID, expect 404 or not found error
  const fakeSessionId = typia.random<string & tags.Format<"uuid">>();
  // Ensure that fakeSessionId is different from created.id
  if (fakeSessionId === created.id) {
    // regenerate to ensure uniqueness for the negative test
    throw new Error("Random session ID collided with existing test data. Re-run the test.");
  }
  await TestValidator.httpError("should error for missing session")(404)(async () => {
    await api.functional.core.sessions.getById(connection, { id: fakeSessionId });
  });
}

