export async function test_create_session_with_valid_invalid_and_duplicate_data(connection: api.IConnection): Promise<void> {
    // 1. Prepare valid session creation data
    const baseInput: ISession.ICreate = {
        user_id: "00000000-0000-0000-0000-000000000001", // Fixed UUID for reproducibility
        token: `session-token-${Date.now()}`,
        expires_at: new Date(Date.now() + 1000 * 60 * 60).toISOString(), // Expires in 1 hour
        device_fingerprint: "fingerprint123",
        ip: "192.168.1.1",
    };

    // 2. Create session successfully
    const created = await api.functional.core.sessions.post(connection, {
        body: baseInput,
    });
    typia.assert<ISession>(created);
    TestValidator.equals("session.user_id")(baseInput.user_id)(created.user_id);
    TestValidator.equals("session.token")(baseInput.token)(created.token);
    TestValidator.equals("session.expires_at")(baseInput.expires_at)(created.expires_at);
    TestValidator.equals("session.device_fingerprint")(baseInput.device_fingerprint)(created.device_fingerprint);
    TestValidator.equals("session.ip")(baseInput.ip)(created.ip);
    // created.id, created.created_at exist
    TestValidator.predicate("created.id should exist")(() => typeof created.id === "string" && !!created.id);
    TestValidator.predicate("created.created_at should be ISO string")(() => typeof created.created_at === "string" && !isNaN(Date.parse(created.created_at)));

    // 3. Validation: Missing required fields (user_id, token, expires_at)
    const missingUserId = { ...baseInput };
