import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { ICoreLocale } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreLocale";
import { IPageICoreLocale } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICoreLocale";

export async function test_list_locales_with_pagination_and_filtering(connection: api.IConnection): Promise<void> {
  // 1. Create multiple unique locale entries
  const sampleLocales: ICoreLocale.ICreate[] = [
    { code: "en-US", name: "English (United States)" },
    { code: "ko-KR", name: "Korean (Korea)" },
    { code: "fr-FR", name: "French (France)" },
    { code: "es-ES", name: "Spanish (Spain)" },
    { code: "ja-JP", name: "Japanese (Japan)" },
  ];
  const createdLocales: ICoreLocale[] = [];
  for (const input of sampleLocales) {
    const out = await api.functional.core.locales.post(connection, { body: input });
    typia.assert<ICoreLocale>(out);
    createdLocales.push(out);
  }

  // 2. Fetch all locales - default pagination (no filters)
  const allRes = await api.functional.core.locales.patch(connection, { body: {} });
  typia.assert<IPageICoreLocale>(allRes);
  TestValidator.predicate("Should return at least the created locales")(() =>
    createdLocales.every(cl => allRes.data.some(al => al.code === cl.code && al.name === cl.name))
  );

  // 3. Fetch with search (by partial code)
  const searchTerm = "ko";
  const searchRes = await api.functional.core.locales.patch(connection, { body: { search: searchTerm } });
  typia.assert<IPageICoreLocale>(searchRes);
  TestValidator.predicate("Should include only matching locales for search")(() =>
    searchRes.data.every(l => l.code.includes(searchTerm) || l.name.toLowerCase().includes(searchTerm))
  );

  // 4. Pagination: limit 2 per page
  const pagedRes = await api.functional.core.locales.patch(connection, { body: { limit: 2, page: 1 } });
  typia.assert<IPageICoreLocale>(pagedRes);
  TestValidator.equals("Should return 2 items on page 1")<number>(2)(pagedRes.data.length <= 2 ? pagedRes.data.length : 2); // up to 2
  TestValidator.predicate("Correct pagination metadata")(() =>
