import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IReviewMedia } from "@ORGANIZATION/PROJECT-api/lib/structures/IReviewMedia";
import typia, { tags } from "typia";

export async function test_update_review_media_metadata_with_various_conditions(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create base review media entry
  const created: IReviewMedia = await api.functional.communicationReview.reviewMedia.post(connection, {
    body: typia.random<IReviewMedia.ICreate>(),
  });
  typia.assert<IReviewMedia>(created);

  // 2. Valid update test (change alt_text and sequence)
  const newAltText = "Updated alt text for a11y compliance";
  const newSequence = created.sequence + 10;
  const updateBody: IReviewMedia.IUpdate = { alt_text: newAltText, sequence: newSequence };

  const updated = await api.functional.communicationReview.reviewMedia.putById(connection, {
    id: created.id,
    body: updateBody,
  });
  typia.assert<IReviewMedia>(updated);
  TestValidator.equals("alt_text updated")(newAltText)(updated.alt_text);
  TestValidator.equals("sequence updated")(newSequence)(updated.sequence);

  // 3. Attempt to update with non-existent/invalid UUID (expect 404)
  const fakeId: string & tags.Format<"uuid"> =
    "01234567-89ab-cdef-0123-456789abcdef" as string & tags.Format<"uuid">;
  await TestValidator.httpError("nonexistent id update should 404")(404)(async () => {
    await api.functional.communicationReview.reviewMedia.putById(connection, {
      id: fakeId,
      body: updateBody,
    });
  });

  // 4a. Field-level validation: sequence as string (should error)
  await TestValidator.error("invalid sequence type should fail")(async () => {
    await api.functional.communicationReview.reviewMedia.putById(connection, {
      id: created.id,
      // @ts-expect-error for test, but typia runtime will catch this
      body: { alt_text: "text", sequence: "not-a-number" },
    });
  });

  // 4b. Field-level: missing sequence & alt_text (both optional, so should succeed)
  const partialUpdate = await api.functional.communicationReview.reviewMedia.putById(connection, {
    id: created.id,
    body: {},
  });
  typia.assert<IReviewMedia>(partialUpdate);

  // 4c. Field-level: null alt_text (which is allowed)
  const nullAltUpdate = await api.functional.communicationReview.reviewMedia.putById(connection, {
    id: created.id,
    body: { alt_text: null },
  });
  typia.assert<IReviewMedia>(nullAltUpdate);
  TestValidator.equals("null alt_text accepted")(null)(nullAltUpdate.alt_text);

  // 5. (Optional) Verify previous updates persist (fields still correct)
  const verify = await api.functional.communicationReview.reviewMedia.putById(connection, {
    id: created.id,
    body: {}, // No change, tries to read the entry
  });
  typia.assert<IReviewMedia>(verify);
  // Since we just did null alt_text update, alt_text should be null.
  TestValidator.equals("verify null alt_text")(null)(verify.alt_text);
}

