import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IReviewModeration } from "@ORGANIZATION/PROJECT-api/lib/structures/IReviewModeration";
import { IPageIReviewModeration } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIReviewModeration";
import { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

function exceptSwagger(key: string): boolean {
  return key === "swagger";
}

export async function test_list_review_moderations_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create multiple diverse review moderations
  const now = new Date();
  const reviewIds = [typia.random<string>(), typia.random<string>()];
  const moderatorIds = [typia.random<string>(), typia.random<string>()];
  const actionTypes = ["remove", "restore", "flag"];

  // Timestamps
  const timestamps = [
    new Date(now.getTime() - 86400_000).toISOString(), // yesterday
    now.toISOString(),
  ];

  // Compose fixtures
  const fixtures: IReviewModeration[] = [];
  for (let i = 0; i < 5; ++i) {
    const input = {
      review_id: reviewIds[i % reviewIds.length] as any,
      moderator_user_id: moderatorIds[i % moderatorIds.length] as any,
      action_type: actionTypes[i % actionTypes.length],
      rationale: `Test rationale ${i}`,
    };
    // Simulate a timestamp (simulate server created_at via PATCH for later filter test)
    const output = await api.functional.communicationReview.reviewModerations.post(
      connection,
      { body: input },
    );
    typia.assert<IReviewModeration>(output);
    // Patch the created_at property so that edge time-range filtering can be tested.
    // In reality, the server controls created_at, so collect what is returned.
    fixtures.push(output);
  }

  // 2. List all records (no filter)
  const resAll = await api.functional.communicationReview.reviewModerations.patch(
    connection,
    { body: {} },
  );
  typia.assert<IPageIReviewModeration>(resAll);
  // All created records should be present
  for (const created of fixtures) {
    TestValidator.predicate("all-listing includes created")(
      resAll.data.some((r) => r.id === created.id),
    );
  }

  // 3. Filter by review_id
  for (const review_id of reviewIds) {
    const result = await api.functional.communicationReview.reviewModerations.patch(
      connection,
      { body: { review_id } },
    );
    typia.assert<IPageIReviewModeration>(result);
    TestValidator.predicate(`all match review_id=${review_id}`)(
      result.data.every((r) => r.review_id === review_id),
    );
  }

  // 4. Filter by moderator_user_id
  for (const moderator_user_id of moderatorIds) {
    const result = await api.functional.communicationReview.reviewModerations.patch(
      connection,
      { body: { moderator_user_id } },
    );
    typia.assert<IPageIReviewModeration>(result);
    TestValidator.predicate(`all match moderator_user_id=${moderator_user_id}`)(
      result.data.every((r) => r.moderator_user_id === moderator_user_id),
    );
  }

  // 5. Filter by action_type
  for (const action_type of actionTypes) {
    const result = await api.functional.communicationReview.reviewModerations.patch(
      connection,
      { body: { action_type } },
    );
    typia.assert<IPageIReviewModeration>(result);
    TestValidator.predicate(`all match action_type=${action_type}`)(
      result.data.every((r) => r.action_type === action_type),
    );
  }

  // 6. Filter by created_at_range (match & empty)
  {
    // Calculate a window covering all created records
    const from = fixtures
      .map((x) => x.created_at)
      .sort()[0];
    const to = fixtures
      .map((x) => x.created_at)
      .sort()
      .reverse()[0];
    // Should match all created records
    const result = await api.functional.communicationReview.reviewModerations.patch(
      connection,
      { body: { created_at_range: { from, to } } },
    );
    typia.assert<IPageIReviewModeration>(result);
    TestValidator.equals("created_at_range matches all")(
      fixtures.map((x) => x.id).sort(),
    )(result.data.map((x) => x.id).sort());

    // Out-of-range query (future only)
    const futureDate = new Date(Date.now() + 1e10).toISOString();
    const resultEmpty = await api.functional.communicationReview.reviewModerations.patch(
      connection,
      { body: { created_at_range: { from: futureDate, to: futureDate } } },
    );
    typia.assert<IPageIReviewModeration>(resultEmpty);
    TestValidator.equals("empty result for future range")(
      [] as string[],
    )(resultEmpty.data.map((x) => x.id));
  }

  // 7. Combined filter (review_id + action_type)
  for (const rv of reviewIds) {
    for (const act of actionTypes) {
      const expected = fixtures.filter((x) => x.review_id === rv && x.action_type === act);
      const result = await api.functional.communicationReview.reviewModerations.patch(
        connection,
        { body: { review_id: rv, action_type: act } },
      );
      typia.assert<IPageIReviewModeration>(result);
      TestValidator.equals(`combined filters [${rv},${act}]`)(
        expected.map((x) => x.id).sort(),
      )(result.data.map((x) => x.id).sort());
    }
  }

  // 8. Pagination: use limit=2, test page 1 and 2, and out-of-bounds
  {
    const limit = 2;
    const records = fixtures.length;
    const pages = Math.ceil(records / limit);
    for (let page = 1; page <= pages + 1; ++page) {
      const result = await api.functional.communicationReview.reviewModerations.patch(
        connection,
        {
          body: {},
        },
      );
      typia.assert<IPageIReviewModeration>(result);
      // Check that pagination object is present and logical
      const p = result.pagination;
      TestValidator.predicate(`pagination.pages logic`)(p.pages >= 1);
      TestValidator.predicate(`pagination.limits`)(p.limit >= 1);
      // If requesting beyond the last page, data should be empty
      if (page > pages) {
        TestValidator.equals("out-of-bounds page empty")([] as string[])(result.data.map((x) => x.id));
      }
    }
  }

  // 9. Edge case: filter with random review_id not present
  const randomReviewId = typia.random<string>();
  TestValidator.equals("nonexistent review id yields empty")([] as string[])(
    (
      await api.functional.communicationReview.reviewModerations.patch(connection, {
        body: { review_id: randomReviewId },
      })
    ).data.map((r) => r.id),
  );
}

