import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IReviewSentiment } from "@ORGANIZATION/PROJECT-api/lib/structures/IReviewSentiment";
import { IPageIReviewSentiment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIReviewSentiment";

export async function test_list_review_sentiments_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a diverse set of test records
  const dataCount = 6;
  const sentiments = ["positive", "negative", "neutral"];
  const created: IReviewSentiment[] = [];

  for (let i = 0; i < dataCount; ++i) {
    // Diversity: alternate review_id, ai_model_version_id, random sentiments, staggered scores/dates
    const base = typia.random<IReviewSentiment.ICreate>();
    const testItem: IReviewSentiment.ICreate = {
      ...base,
      sentiment: sentiments[i % sentiments.length],
      score: 60 + i * 5, // e.g. 60, 65, 70 ...
      analyzed_at: new Date(Date.now() - i * 36e5).toISOString(), // decrement hours for uniqueness
    };
    const rec = await api.functional.communicationReview.reviewSentiments.post(connection, { body: testItem });
    typia.assert(rec);
    created.push(rec);
  }

  // 2. List all records - no filter
  const allResp = await api.functional.communicationReview.reviewSentiments.patch(connection, { body: {} });
  typia.assert<IPageIReviewSentiment>(allResp);
  TestValidator.predicate("should list at least as many as we created")(
    () => allResp.pagination.records >= created.length,
  );
  TestValidator.equals("pagination current page should be 1")(1)(allResp.pagination.current);
  // default limit check (implementation-dependent: may be > dataCount)
  TestValidator.predicate("data array length <= pagination.limit")(
    () => allResp.data.length <= allResp.pagination.limit,
  );

  // 3.a Filter by sentiment
  const filterSentiment = sentiments[1];
  const filteredSent = await api.functional.communicationReview.reviewSentiments.patch(connection, { body: { sentiment: filterSentiment } });
  typia.assert<IPageIReviewSentiment>(filteredSent);
  TestValidator.predicate(`sentiment filter matches only sentiment=${filterSentiment}`)(
    () => filteredSent.data.every(r => r.sentiment === filterSentiment),
  );

  // 3.a Edge case: non-existent sentiment
  const noneResp = await api.functional.communicationReview.reviewSentiments.patch(connection, { body: { sentiment: "does_not_exist" } });
  typia.assert<IPageIReviewSentiment>(noneResp);
  TestValidator.equals("no results for nonsense sentiment")([] as IReviewSentiment[])(noneResp.data);

  // 3.b Filter by review_id
  const reviewFilterId = created[0].review_id;
  const filteredByReview = await api.functional.communicationReview.reviewSentiments.patch(connection, { body: { review_id: reviewFilterId } });
  typia.assert<IPageIReviewSentiment>(filteredByReview);
  TestValidator.predicate("all records have requested review_id")(
    () => filteredByReview.data.every(r => r.review_id === reviewFilterId),
  );

  // 3.b Combine review_id + ai_model_version_id
  const aiModelId = created[1].ai_model_version_id;
  const filteredCombo = await api.functional.communicationReview.reviewSentiments.patch(connection, { body: { review_id: created[1].review_id, ai_model_version_id: aiModelId } });
  typia.assert<IPageIReviewSentiment>(filteredCombo);
  TestValidator.predicate("all records match both review_id and ai_model_version_id")(
    () => filteredCombo.data.every(r => r.review_id === created[1].review_id && r.ai_model_version_id === aiModelId),
  );

  // 3.c Score filter
  const minScore = created[2].score;
  const filteredScore = await api.functional.communicationReview.reviewSentiments.patch(connection, { body: { score_min: minScore } });
  typia.assert<IPageIReviewSentiment>(filteredScore);
  TestValidator.predicate("all records >= score_min")(
    () => filteredScore.data.every(r => r.score >= minScore),
  );

  // 3.c Date range
  const dateFrom = created[created.length - 1].analyzed_at;
  const filteredDate = await api.functional.communicationReview.reviewSentiments.patch(connection, { body: { analyzed_at_from: dateFrom } });
  typia.assert<IPageIReviewSentiment>(filteredDate);
  TestValidator.predicate("all analyzed_at >= dateFrom")(
    () => filteredDate.data.every(r => r.analyzed_at >= dateFrom),
  );

  // 3.d Pagination: request high page number (expect empty or valid resp)
  const outOfBounds = await api.functional.communicationReview.reviewSentiments.patch(connection, { body: { page: 9999 } });
  typia.assert<IPageIReviewSentiment>(outOfBounds);
  TestValidator.equals("no data for out-of-bounds page")([] as IReviewSentiment[])(outOfBounds.data);
  TestValidator.predicate("pagination pages >= 1")(
    () => outOfBounds.pagination.pages >= 1,
  );
  TestValidator.equals("pagination current matches requested")(
    9999,
  )(outOfBounds.pagination.current);

  // 5. Negative: random, no-match uuid for review_id
  const randomUuid = "00000000-0000-0000-0000-000000000000";
  const noMatch = await api.functional.communicationReview.reviewSentiments.patch(connection, { body: { review_id: randomUuid } });
  typia.assert<IPageIReviewSentiment>(noMatch);
  TestValidator.equals("no data for non-existent review_id")([] as IReviewSentiment[])(noMatch.data);
  TestValidator.predicate("pagination metadata valid on no-match")(
    () => typeof noMatch.pagination === "object" && noMatch.pagination !== null,
  );
}

