import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { ISentimentAnalysisLog } from "@ORGANIZATION/PROJECT-api/lib/structures/ISentimentAnalysisLog";
import { IPageISentimentAnalysisLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageISentimentAnalysisLog";

export async function test_list_sentiment_analysis_logs_with_filters_and_pagination(
  connection: api.IConnection
): Promise<void> {
  // Step 1: Create test entries
  const baseTime = new Date();
  const logVariants: ISentimentAnalysisLog.ICreate[] = [
    {
      review_id: typia.random<string>(),
      ai_model_version_id: typia.random<string>(),
      model_version: "ver-1",
      raw_input: "this is a great product!",
      output: "positive",
      analyzed_at: new Date(baseTime.getTime() - 100000).toISOString(),
    },
    {
      review_id: typia.random<string>(),
      ai_model_version_id: typia.random<string>(),
      model_version: "ver-1",
      raw_input: "not what I expected",
      output: "negative",
      analyzed_at: new Date(baseTime.getTime() - 80000).toISOString(),
    },
    {
      review_id: typia.random<string>(),
      ai_model_version_id: typia.random<string>(),
      model_version: "ver-2",
      raw_input: "average experience",
      output: "neutral",
      analyzed_at: new Date(baseTime.getTime() - 60000).toISOString(),
    },
  ];

  const posted: ISentimentAnalysisLog[] = [];
  for (const variant of logVariants) {
    const created = await api.functional.communicationReview.sentimentAnalysisLogs.post(
      connection,
      { body: variant }
    );
    posted.push(created);
  }

  // Step 2: List all, default pagination/filter
  const listAll = await api.functional.communicationReview.sentimentAnalysisLogs.patch(
    connection,
    { body: {} }
  );
  typia.assert<IPageISentimentAnalysisLog>(listAll);
  // Check all created logs are present
  for (const log of posted) {
    TestValidator.predicate("created log present")(!!listAll.data.find((x) => x.id === log.id));
  }

  // Step 3: Filter by review_id
  const filterByReview = await api.functional.communicationReview.sentimentAnalysisLogs.patch(
    connection,
    { body: { review_id: posted[0].review_id } }
  );
  typia.assert<IPageISentimentAnalysisLog>(filterByReview);
  TestValidator.predicate("only expected review_id")(
    filterByReview.data.every((x) => x.review_id === posted[0].review_id)
  );

  // Step 4: Filter by non-existent ai_model_version_id (zero results)
  const fakeModelId = typia.random<string>();
  const noMatch = await api.functional.communicationReview.sentimentAnalysisLogs.patch(
    connection,
    { body: { ai_model_version_id: fakeModelId } }
  );
  typia.assert<IPageISentimentAnalysisLog>(noMatch);
  TestValidator.equals("zero results")([] as ISentimentAnalysisLog[])(noMatch.data);

  // Step 5: Exceed page count
  const overPage = await api.functional.communicationReview.sentimentAnalysisLogs.patch(
    connection,
    { body: { page: 1000, limit: 1 } }
  );
  typia.assert<IPageISentimentAnalysisLog>(overPage);
  TestValidator.equals("oversized page yields empty")([] as ISentimentAnalysisLog[])(overPage.data);

  // Step 6: Filter by tight timestamp window (should catch only the last one)
  const timeFrom = new Date(baseTime.getTime() - 70000).toISOString();
  const timeTo = new Date(baseTime.getTime() - 50000).toISOString();
  const timeFiltered = await api.functional.communicationReview.sentimentAnalysisLogs.patch(
    connection,
    { body: { time_from: timeFrom, time_to: timeTo } }
  );
  typia.assert<IPageISentimentAnalysisLog>(timeFiltered);
  TestValidator.predicate("timestamps between")(
    timeFiltered.data.every(
      (x) => x.analyzed_at >= timeFrom && x.analyzed_at <= timeTo
    )
  );

  // Step 7: input_match filter
  const inputPartial = "great";
  const matchInput = await api.functional.communicationReview.sentimentAnalysisLogs.patch(
    connection,
    { body: { input_match: inputPartial } }
  );
  typia.assert<IPageISentimentAnalysisLog>(matchInput);
  TestValidator.predicate("input_match subset")(
    matchInput.data.every((x) => x.raw_input.includes(inputPartial))
  );

  // Step 8: Validate pagination fields
  TestValidator.predicate("pagination shape")(
    typeof listAll.pagination?.current === "number" &&
    typeof listAll.pagination?.limit === "number" &&
    typeof listAll.pagination?.records === "number" &&
    typeof listAll.pagination?.pages === "number"
  );

  // Step 9: Submit malformed filter (time_from > time_to) -- expect empty or error
  const wrong = await api.functional.communicationReview.sentimentAnalysisLogs.patch(
    connection,
    { body: {
      time_from: new Date(baseTime.getTime()).toISOString(),
      time_to: new Date(baseTime.getTime() - 86400000).toISOString(), // 1 day earlier
    } }
  );
  typia.assert<IPageISentimentAnalysisLog>(wrong);
  TestValidator.equals("malformed filter yields empty")([] as ISentimentAnalysisLog[])(wrong.data);
}

