  typia.assert<ISentimentAnalysisLog>(created);

  // Step 2: Retrieve by valid ID
  const retrieved: ISentimentAnalysisLog = await api.functional.communicationReview.sentimentAnalysisLogs.getById(connection, { id: created.id });
  typia.assert<ISentimentAnalysisLog>(retrieved);
  TestValidator.equals("retrieved log matches created", exceptKeys())(created)(retrieved);

  // Step 3: Attempt to retrieve by an invalid/non-existent ID
  const invalidId = typia.random<string & tags.Format<"uuid">>();
  // Ensure the invalidId is not the same as created.id
  if (invalidId === created.id) {
    // regenerate invalidId
    return await test_get_sentiment_analysis_log_by_valid_and_invalid_id(connection);
  }

  await TestValidator.httpError("404 on not found")(404)(async () => {
    await api.functional.communicationReview.sentimentAnalysisLogs.getById(connection, { id: invalidId });
  });
}

function exceptKeys(): (key: string) => boolean {
  // No system fields to ignore, but can be extended if API adds metadata.
  return (key: string): boolean => false;
}

