import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";
import { IFavorite } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavorite";
import { IPageIFavorite } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIFavorite";

function makeFavoriteCreateInput(user_id: string, product_id: string): IFavoritesPersonalization.IFavoriteCreate {
    return { user_id, product_id };
}

export async function test_list_favorites_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
    // --- Data Preparation ---
    // Simulate two users and three products
    const userA = typia.random<IFavoritesPersonalization.IFavoriteCreate>().user_id;
    const userB = typia.random<IFavoritesPersonalization.IFavoriteCreate>().user_id;
    const product1 = typia.random<IFavoritesPersonalization.IFavoriteCreate>().product_id;
    const product2 = typia.random<IFavoritesPersonalization.IFavoriteCreate>().product_id;
    const product3 = typia.random<IFavoritesPersonalization.IFavoriteCreate>().product_id;

    // Create favorites for combinations
    const createdFavorites: IFavoritesPersonalization.IFavorite[] = [];
    for (const [user, product] of [
        [userA, product1],
        [userA, product2],
        [userB, product2],
        [userB, product3]
    ] as [string, string][]) {
        const fav = await api.functional.favoritesPersonalization.favorites.post(connection, {
            body: makeFavoriteCreateInput(user, product)
        });
        typia.assert(fav);
        createdFavorites.push(fav);
    }

    // Soft-delete a favorite by simulation (for edge case)
    // (Assume: no real API for delete, so we only use existing data for active vs soft-delete)

    // --- Filtering and Pagination ---

    // 1. Filter by user_id
    let res = await api.functional.favoritesPersonalization.favorites.patch(connection, { body: { user_id: userA } });
    typia.assert<IPageIFavorite>(res);
    TestValidator.predicate("Filter by user_id")(res.data.every(fav => fav.user_id === userA));

    // 2. Filter by product_id
    res = await api.functional.favoritesPersonalization.favorites.patch(connection, { body: { product_id: product2 } });
    typia.assert<IPageIFavorite>(res);
    TestValidator.predicate("Filter by product_id")(res.data.every(fav => fav.product_id === product2));

    // 3. Filter by user and product
    res = await api.functional.favoritesPersonalization.favorites.patch(connection, { body: { user_id: userB, product_id: product3 } });
    typia.assert<IPageIFavorite>(res);
    TestValidator.predicate("Filter by user and product")(res.data.every(fav => fav.user_id === userB && fav.product_id === product3));

    // 4. Pagination: use limit 1, get page 2
    res = await api.functional.favoritesPersonalization.favorites.patch(connection, { body: { limit: 1, page: 2 } });
    typia.assert<IPageIFavorite>(res);
    TestValidator.predicate("Pagination test - limit and page")(res.pagination.current === 2 && res.pagination.limit === 1);

    // 5. Page request beyond available (e.g., page=100)
    res = await api.functional.favoritesPersonalization.favorites.patch(connection, { body: { page: 100, limit: 1 } });
    typia.assert<IPageIFavorite>(res);
    TestValidator.equals("Empty results for out-of-bounds page")([] as IFavorite[])(res.data);

    // 6. No results: filter with non-existent user_id
    const fakeUser = typia.random<IFavoritesPersonalization.IFavoriteCreate>().user_id;
    res = await api.functional.favoritesPersonalization.favorites.patch(connection, { body: { user_id: fakeUser } });
    typia.assert<IPageIFavorite>(res);
    TestValidator.equals("No results for fake user")([] as IFavorite[])(res.data);

    // 7. Edge case: filter by created_at period
    const onePast = new Date(Date.now() - 24*60*60*1000).toISOString();
    res = await api.functional.favoritesPersonalization.favorites.patch(connection, { body: { created_at_from: onePast, created_at_to: new Date().toISOString() } });
    typia.assert<IPageIFavorite>(res);
    TestValidator.predicate("created_at period filter")(res.data.every(fav => new Date(fav.created_at) >= new Date(onePast)));

    // 8. Invalid filter: wrong uuid format
    await TestValidator.httpError("invalid user_id format")(400)(async () => {
        await api.functional.favoritesPersonalization.favorites.patch(connection, { body: { user_id: "not-a-uuid" } });
    });
}

