export async function test_update_favorite_by_id_with_various_conditions(connection: api.IConnection): Promise<void> {
  // 1. Create a valid favorite (obtain a real ID)
  const createInput: IFavoritesPersonalization.IFavoriteCreate = typia.random<IFavoritesPersonalization.IFavoriteCreate>();
  const favorite: IFavoritesPersonalization.IFavorite = await api.functional.favoritesPersonalization.favorites.post(connection, { body: createInput });
  typia.assert<IFavoritesPersonalization.IFavorite>(favorite);

  // 2. Update the created favorite: soft-delete (set deleted_at)
  const deletedAt = new Date().toISOString();
  const updateInput: IFavoritesPersonalization.IFavoriteUpdate = { deleted_at: deletedAt };
  const updated = await api.functional.favoritesPersonalization.favorites.putById(connection, {
    id: favorite.id,
    body: updateInput,
  });
  typia.assert<IFavoritesPersonalization.IFavorite>(updated);
  TestValidator.equals("deleted_at updated")<string | null>(deletedAt)(updated.deleted_at ?? null);
  TestValidator.equals("id unchanged")<string>(favorite.id)(updated.id);

  // 3. Update a non-existent favorite (random UUID, expect error)
  await TestValidator.httpError("Update non-existent favorite should fail")(404, 400)(async () =>
    api.functional.favoritesPersonalization.favorites.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(),
      body: updateInput,
    })
  );

  // 4. Update a logically deleted favorite (try resoft-delete, expect error)
  await TestValidator.httpError("Update already deleted favorite should fail")(404, 400)(async () =>
    api.functional.favoritesPersonalization.favorites.putById(connection, {
      id: favorite.id,
      body: { deleted_at: new Date(Date.now() + 3600000).toISOString() }, // new timestamp
    })
  );

  // 5. Attempt to update immutable fields by forcibly casting body (should fail by validation)
  const invalidInput = {
    deleted_at: new Date().toISOString(),
    user_id: favorite.user_id,
    product_id: favorite.product_id,
  } as any; // send forbidden fields deliberately
  await TestValidator.error("Immutable fields (user_id, product_id) should not be allowed")(() =>
    typia.assert<IFavoritesPersonalization.IFavoriteUpdate>(invalidInput)
  );
}

