export async function test_list_bookmarks_with_filters_and_pagination(
  connection: api.IConnection
): Promise<void> {
  // 1. Data preparation: prepare sample values
  const user_id = typia.random<string & typia.tags.Format<"uuid">>();
  const productA = typia.random<string & typia.tags.Format<"uuid">>();
  const productB = typia.random<string & typia.tags.Format<"uuid">>();
  const custom_folder1 = "folder-one";
  const custom_folder2 = "folder-two";
  const tag1 = "tag-first";
  const tag2 = "tag-second";
  const note1 = "quick note";
  const note2 = "detailed note";

  // Bookmarks to insert
  const bookmarksToCreate: IFavoritesPersonalization.IBookmarkCreate[] = [
    { user_id, product_id: productA, custom_folder: custom_folder1, tag: tag1, note: note1 },
    { user_id, product_id: productA, custom_folder: custom_folder2, tag: tag2, note: note2 },
    { user_id, product_id: productB, custom_folder: custom_folder1, tag: tag1, note: note2 },
    { user_id, product_id: productB, custom_folder: custom_folder1, tag: tag2, note: note1 },
    { user_id, product_id: productA, custom_folder: custom_folder2, tag: tag1, note: note2 },
    { user_id, product_id: productB, custom_folder: custom_folder2, tag: tag2, note: note2 },
  ];

  const createdBookmarks: IFavoritesPersonalization.IBookmark[] = [];

  for (const input of bookmarksToCreate) {
    const created = await api.functional.favoritesPersonalization.bookmarks.post(connection, { body: input });
    typia.assert<IFavoritesPersonalization.IBookmark>(created);
    createdBookmarks.push(created);
  }

  // 2. List all bookmarks with no filters
  const listAll = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
    body: { user_id }
  });
  typia.assert<IFavoritesPersonalization.PageIBookmark>(listAll);
  TestValidator.equals("all bookmarks count")(
    createdBookmarks.length
  )(listAll.pagination.records);
  TestValidator.equals("all bookmarks page data")(
    createdBookmarks.length
  )(listAll.data.length);

  // 3. Filter by product_id
  const listProductA = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
    body: { user_id, product_id: productA }
  });
  typia.assert<IFavoritesPersonalization.PageIBookmark>(listProductA);
  for (const bm of listProductA.data) {
    TestValidator.equals("product id filtered")(productA)(bm.product_id);
  }

  // 3. Filter by custom_folder
  const listFolder1 = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
    body: { user_id, custom_folder: custom_folder1 }
  });
  typia.assert<IFavoritesPersonalization.PageIBookmark>(listFolder1);
  for (const bm of listFolder1.data) {
    TestValidator.equals("folder filtered")(custom_folder1)(bm.custom_folder);
  }

  // 3. Filter by tag
  const listTag2 = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
    body: { user_id, tag: tag2 }
  });
  typia.assert<IFavoritesPersonalization.PageIBookmark>(listTag2);
  for (const bm of listTag2.data) {
    TestValidator.equals("tag filtered")(tag2)(bm.tag);
  }

  // 3. Filter by note
  const listNote1 = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
    body: { user_id, note: note1 }
  });
  typia.assert<IFavoritesPersonalization.PageIBookmark>(listNote1);
  for (const bm of listNote1.data) {
    TestValidator.equals("note filtered")(note1)(bm.note);
  }

  // 4. Pagination: page 1 (limit=2)
  const pageLimit = 2;
  const page1 = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
    body: { user_id, page: 1, limit: pageLimit }
  });
  typia.assert<IFavoritesPersonalization.PageIBookmark>(page1);
  TestValidator.equals("page 1 count")(Math.min(pageLimit, createdBookmarks.length))(page1.data.length);
  TestValidator.equals("pagination current page")(1)(page1.pagination.current);
  TestValidator.equals("pagination limit")(pageLimit)(page1.pagination.limit);

  // 4. Pagination: page 2
  const page2 = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
    body: { user_id, page: 2, limit: pageLimit }
  });
  typia.assert<IFavoritesPersonalization.PageIBookmark>(page2);
  TestValidator.equals("pagination current page 2")(2)(page2.pagination.current);
  // No overlap between page1 and page2 data ids
  const page1Ids = page1.data.map(bm => bm.id);
  for (const bm of page2.data) {
    TestValidator.predicate("no overlap ids")(() => !page1Ids.includes(bm.id));
  }

  // 5. Edge Case: filter that matches nothing
  const emptyList = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
    body: { user_id, tag: "not-realtag-xyz" }
  });
  typia.assert<IFavoritesPersonalization.PageIBookmark>(emptyList);
  TestValidator.equals("empty filter result count")(0)(emptyList.data.length);
  TestValidator.equals("empty filter pagination records")(0)(emptyList.pagination.records);

  // 5. Edge Case: out-of-range page
  const outOfRange = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
    body: { user_id, page: 1000, limit: pageLimit }
  });
  typia.assert<IFavoritesPersonalization.PageIBookmark>(outOfRange);
  TestValidator.equals("out of range page count")(0)(outOfRange.data.length);

  // 9. Validate pagination objects
  const validatePagination = (pagination: IPage.IPagination) => {
    typia.assert<IPage.IPagination>(pagination);
    TestValidator.predicate("min page is 1")(() => pagination.current >= 1);
    TestValidator.predicate("limit positive")(() => pagination.limit >= 1);
    TestValidator.predicate("records non-negative")(() => pagination.records >= 0);
    TestValidator.predicate("pages non-negative")(() => pagination.pages >= 0);
  };
  validatePagination(listAll.pagination);
  validatePagination(page1.pagination);
  validatePagination(page2.pagination);
  validatePagination(emptyList.pagination);
  validatePagination(outOfRange.pagination);

  // 10. Negative case: invalid page (zero/negative)
  await TestValidator.error("page=0 triggers error or empty")(
    async () => {
      const r = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
        body: { user_id, page: 0, limit: pageLimit }
      });
      // Acceptable: either error thrown, or returns empty data
      typia.assert<IFavoritesPersonalization.PageIBookmark>(r);
      TestValidator.equals("page=0 empty result")(0)(r.data.length);
    }
  );

  await TestValidator.error("negative page triggers error or empty")(
    async () => {
      const r = await api.functional.favoritesPersonalization.bookmarks.patch(connection, {
        body: { user_id, page: -1, limit: pageLimit }
      });
      // Acceptable: either error thrown, or returns empty data
      typia.assert<IFavoritesPersonalization.PageIBookmark>(r);
      TestValidator.equals("negative page empty result")(0)(r.data.length);
    }
  );
}

