import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";
import typia, { tags } from "typia";

export async function test_update_bookmark_with_various_conditions(connection: api.IConnection): Promise<void> {
    // 1. Create a valid bookmark
    const bookmarkInput: IFavoritesPersonalization.IBookmarkCreate = {
        user_id: typia.random<string & tags.Format<"uuid">>(),
        product_id: typia.random<string & tags.Format<"uuid">>(),
        custom_folder: "InitialFolder",
        tag: "important",
        note: "My note",
    };
    const created = await api.functional.favoritesPersonalization.bookmarks.post(connection, { body: bookmarkInput });
    typia.assert<IFavoritesPersonalization.IBookmark>(created);
    TestValidator.equals("created.product_id")(bookmarkInput.product_id)(created.product_id);
    TestValidator.equals("created.user_id")(bookmarkInput.user_id)(created.user_id);

    // 2. Update mutable fields
    const updateBody: IFavoritesPersonalization.IBookmarkUpdate = {
        custom_folder: "UpdatedFolder",
        tag: "urgent",
        note: "Updated note"
    };
    const updated = await api.functional.favoritesPersonalization.bookmarks.putById(connection, { id: created.id, body: updateBody });
    typia.assert<IFavoritesPersonalization.IBookmark>(updated);
    TestValidator.equals("update.custom_folder")("UpdatedFolder")(updated.custom_folder);
    TestValidator.equals("update.tag")("urgent")(updated.tag);
    TestValidator.equals("update.note")("Updated note")(updated.note);
    // Immutable fields should not change
    TestValidator.equals("immutable user_id")(created.user_id)(updated.user_id);
    TestValidator.equals("immutable product_id")(created.product_id)(updated.product_id);

    // 3. Attempt update with invalid/non-existent ID
    const invalidId = typia.random<string & tags.Format<"uuid">>();
    await TestValidator.httpError("update with invalid id")(404)(async () => {
        await api.functional.favoritesPersonalization.bookmarks.putById(connection, { id: invalidId, body: updateBody });
    });

    // 4. Soft-delete the bookmark and try updating
    const deleteTime = new Date().toISOString();
    const delResult = await api.functional.favoritesPersonalization.bookmarks.putById(connection, { id: created.id, body: { deleted_at: deleteTime } });
    typia.assert<IFavoritesPersonalization.IBookmark>(delResult);
    TestValidator.equals("deleted_at")(deleteTime)(delResult.deleted_at);

    // After soft deletion, attempt another update and expect 404 or failure
    await TestValidator.httpError("update after soft delete")(404)(async () => {
        await api.functional.favoritesPersonalization.bookmarks.putById(connection, { id: created.id, body: { note: "Should not update" } });
    });

    // 5. Attempt to change immutable fields (should fail or ignore)
    const illegalUpdate = {
        // @ts-expect-error: purposely try illegal fields
        user_id: typia.random<string & tags.Format<"uuid">>(),
        // @ts-expect-error: purposely try illegal fields
        product_id: typia.random<string & tags.Format<"uuid">>(),
        custom_folder: "AnotherFolder",
        tag: "illegal-attempt"
    };
    await TestValidator.error("attempt illegal update fields")(() =>
      // Type error expected by typescript, runtime should ignore or fail
      api.functional.favoritesPersonalization.bookmarks.putById(connection, { id: created.id, body: illegalUpdate as any })
    );
}

