import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPageICatalogEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICatalogEvidence";
import { ICatalogEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/ICatalogEvidence";
import typia from "typia";

export async function test_list_catalog_evidence_with_various_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // (A) Happy path: fetch with description substring (if any exist)
  const happyInput: ICatalogEvidence.IRequest = {
    description: "test", // Assuming at least one record will match 'test'
    page: 1,
    limit: 5,
  };
  const happyResult = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: happyInput });
  typia.assert<IPageICatalogEvidence>(happyResult);
  // Assert each data item contains the filter in description
  const happyData: ICatalogEvidence[] = happyResult.data ?? [];
  for (const row of happyData) {
    TestValidator.predicate("Each result should include filter in description")(
      () => row.description.includes("test"),
    );
  }
  // Sanity: happy path pagination and range
  if (happyResult.pagination) {
    TestValidator.predicate("Page >= 1")(() => happyResult.pagination!.current >= 1);
    TestValidator.predicate("Limit nonzero")(() => happyResult.pagination!.limit > 0);
  }

  // (B) Pagination checks
  const limit = 1;
  const page1Input: ICatalogEvidence.IRequest = {
    page: 1,
    limit,
  };
  const page2Input: ICatalogEvidence.IRequest = {
    page: 2,
    limit,
  };
  const page1 = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: page1Input });
  const page2 = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: page2Input });
  typia.assert<IPageICatalogEvidence>(page1);
  typia.assert<IPageICatalogEvidence>(page2);
  const row1 = (page1.data && page1.data[0]) || null;
  const row2 = (page2.data && page2.data[0]) || null;
  // When both pages have data, row1 and row2 should not have same ID
  if (row1 && row2) {
    TestValidator.predicate("Rows on subsequent pages should differ")(() => row1.id !== row2.id);
  }
  // Pagination info
  if (page1.pagination && page2.pagination) {
    TestValidator.predicate("Page sequence")(() =>
      page1.pagination!.current + 1 === page2.pagination!.current,
    );
  }

  // (C) Empty result case
  const emptyInput: ICatalogEvidence.IRequest = {
    description: "__no_record_should_match_this__",
    page: 1,
    limit: 5,
  };
  const emptyRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: emptyInput });
  typia.assert<IPageICatalogEvidence>(emptyRes);
  const emptyArr: ICatalogEvidence[] = emptyRes.data ?? [];
  TestValidator.equals("Empty data list")([] as ICatalogEvidence[])(emptyArr);
  if (emptyRes.pagination) {
    TestValidator.predicate("Empty result: pages >=1")(() => emptyRes.pagination!.pages >= 1);
  }

  // (D) Boundary/invalid page
  const invalidPageInput: ICatalogEvidence.IRequest = {
    page: 0,
    limit: 5,
  };
  await TestValidator.httpError("invalid page should fail")(400)(async () => {
    await api.functional.productCatalog.catalogEvidence.patch(connection, { body: invalidPageInput });
  });

  // (E) Invalid filter field (simulate: send unsupported property)
  const invalidFieldInput = {
    description: "test",
    page: 1,
    limit: 5,
    unknown_field: "should cause error", // Not in DTO
  } as any;
  await TestValidator.httpError("unexpected property in filter should fail")(400)(async () => {
    await api.functional.productCatalog.catalogEvidence.patch(connection, { body: invalidFieldInput });
  });
}

