import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { ICatalogEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/ICatalogEvidence";

export async function test_update_catalog_evidence_by_id_with_validation_and_not_found_case(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a catalog evidence record (for update test)
  const createInput: ICatalogEvidence.ICreate = typia.random<ICatalogEvidence.ICreate>();
  const created: ICatalogEvidence = await api.functional.productCatalog.catalogEvidence.post(connection, { body: createInput });
  typia.assert(created);

  // 2. Positive update (update description only)
  const newDescription = "Updated evidence description for compliance.";
  const updateInput: ICatalogEvidence.IUpdate = { description: newDescription };
  const updated = await api.functional.productCatalog.catalogEvidence.putById(connection, {
    id: created.id,
    body: updateInput,
  });
  typia.assert(updated);

  // Check only allowed field changes, immutable fields unchanged
  TestValidator.equals("description updated")<string | null>(newDescription)(updated.description);
  TestValidator.equals("reference_uri unchanged")<string>(created.reference_uri)(updated.reference_uri);
  TestValidator.equals("catalog_event_id unchanged")<(string & tags.Format<"uuid">) | null>(created.catalog_event_id ?? null)(updated.catalog_event_id ?? null);

  // 3. Not-found update (random UUID)
  const randomUUID = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("should error for not-found: 404")(404)(async () => {
    await api.functional.productCatalog.catalogEvidence.putById(connection, {
      id: randomUUID,
      body: updateInput,
    });
  });

  // 4. Validation error: description as number (invalid type)
  await TestValidator.httpError("should error for invalid type: 400/422")(400,422)(async () => {
    await api.functional.productCatalog.catalogEvidence.putById(connection, {
      id: created.id,
      body: { description: 123 as any },
    });
  });

  // 5. Validation error: description too short/null (if null not allowed)
  await TestValidator.httpError("should error when setting description as null: 400/422")(400,422)(async () => {
    await api.functional.productCatalog.catalogEvidence.putById(connection, {
      id: created.id,
      body: { description: null },
    });
  });

  // 6. Forbidden/immutable field update: try to update reference_uri (should be ignored or error)
  await TestValidator.error("should not allow forbidden field change")(() =>
    api.functional.productCatalog.catalogEvidence.putById(connection, {
      id: created.id,
      body: { ...(updateInput as any), reference_uri: "https://forbidden.com/evidence" } as any,
    })
  );
}

