import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { ICart } from "@ORGANIZATION/PROJECT-api/lib/structures/ICart";
import { IPageICart } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICart";

export async function test_list_carts_with_filters_and_deleted_cart_handling(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create several (3) cart records
  const userIds: (string & tags.Format<"uuid">)[] = [
    typia.random<string & tags.Format<"uuid">>(),
    typia.random<string & tags.Format<"uuid">>(),
    typia.random<string & tags.Format<"uuid">>(),
  ];
  const sessionIds: ((string & tags.Format<"uuid">) | null)[] = [
    typia.random<string & tags.Format<"uuid">>(),
    null,
    typia.random<string & tags.Format<"uuid">>(),
  ];
  const carts: ICart[] = [];
  for (let i = 0; i < 3; ++i) {
    const cart = await api.functional.cartOrder.cart.post(connection, {
      body: {
        user_id: userIds[i],
        session_id: sessionIds[i],
      },
    });
    typia.assert<ICart>(cart);
    carts.push(cart);
  }

  // 2. Soft-delete one cart
  const deletedCart = carts[1];
  const deleteResult = await api.functional.cartOrder.cart.eraseById(connection, {
    id: deletedCart.id,
  });
  typia.assert<ICart.IDeleteResult>(deleteResult);

  // 3. List all active (non-deleted) carts (deleted_at_exists: false)
  const activeList = await api.functional.cartOrder.cart.patch(connection, {
    body: {
      deleted_at_exists: false,
    },
  });
  typia.assert<IPageICart>(activeList);
  const activeCarts: ICart[] = activeList.data ?? [];
  TestValidator.predicate("soft-deleted cart not in active list")(
    () => !activeCarts.some((c) => c.id === deletedCart.id)
  );

  // 4. List only deleted carts (deleted_at_exists: true) - should include the deleted one
  const deletedList = await api.functional.cartOrder.cart.patch(connection, {
    body: {
      deleted_at_exists: true,
    },
  });
  typia.assert<IPageICart>(deletedList);
  const deletedCarts: ICart[] = deletedList.data ?? [];
  TestValidator.predicate("deleted cart appears in deleted list")(
    () => deletedCarts.some((c) => c.id === deletedCart.id)
  );

  // 5. List carts with filter for user_id
  {
    const res = await api.functional.cartOrder.cart.patch(connection, {
      body: {
        user_id: carts[0].user_id,
        deleted_at_exists: false,
      },
    });
    typia.assert<IPageICart>(res);
    const data: ICart[] = res.data ?? [];
    TestValidator.predicate("filter by user_id returns correct cart")(
      () => data.some((c) => c.user_id === carts[0].user_id && !c.deleted_at)
    );
  }
  // 5b. List carts with filter for session_id
  {
    const sessionId = carts[2].session_id;
    if (sessionId) {
      const res = await api.functional.cartOrder.cart.patch(connection, {
        body: {
          session_id: sessionId,
          deleted_at_exists: false,
        },
      });
      typia.assert<IPageICart>(res);
      const data: ICart[] = res.data ?? [];
      TestValidator.predicate("filter by session_id returns correct cart")(
        () => data.some((c) => c.session_id === sessionId && !c.deleted_at)
      );
    }
  }
  // 6. Filter that should yield no results (unknown user_id)
  {
    const res = await api.functional.cartOrder.cart.patch(connection, {
      body: {
        user_id: typia.random<string & tags.Format<"uuid">>(),
        deleted_at_exists: false,
      },
    });
    typia.assert<IPageICart>(res);
    const data: ICart[] = res.data ?? [];
    TestValidator.equals("no result for random user_id")<ICart[]>([])(data);
  }
  // 6b. Filter by known deleted cart with deleted_at_exists: false yields no results
  {
    const res = await api.functional.cartOrder.cart.patch(connection, {
      body: {
        user_id: deletedCart.user_id,
        deleted_at_exists: false,
      },
    });
    typia.assert<IPageICart>(res);
    const data: ICart[] = res.data ?? [];
    TestValidator.equals("no result for deleted cart in active list")<ICart[]>([])(data);
  }
  // 7. Invalid filter values (malformed uuid)
  await TestValidator.httpError("malformed user_id rejected")(400)(async () => {
    await api.functional.cartOrder.cart.patch(connection, {
      body: {
        user_id: "not-a-uuid" as any,
      },
    });
  });

  // 8. Pagination: create one more, check listing with limit=2
  const addedCart = await api.functional.cartOrder.cart.post(connection, {
    body: {
      user_id: typia.random<string & tags.Format<"uuid">>(),
      session_id: null,
    },
  });
  typia.assert<ICart>(addedCart);
  const paged = await api.functional.cartOrder.cart.patch(connection, {
    body: {
      limit: 2,
      page: 1,
      deleted_at_exists: false,
    },
  });
  typia.assert<IPageICart>(paged);
  TestValidator.predicate("pagination returns at most 2 records")(
    () => (paged.data?.length ?? 0) <= 2
  );
  TestValidator.predicate("pagination meta matches limit")(
    () => paged.pagination?.limit === 2
  );
}

