export async function test_create_cart_with_valid_invalid_and_duplicate_constraints(
  connection: api.IConnection,
): Promise<void> {
  // Helper to generate a valid UUID
  const uuid = (): string =>
    "00000000-0000-4000-8000-" + Math.floor(Math.random() * 1e12).toString().padStart(12, "0");

  // Minimal valid cart creation (required fields only)
  const minimal: ICart.ICreate = {
    user_id: uuid(),
  };
  const cartMinimal = await api.functional.cartOrder.cart.post(connection, { body: minimal });
  typia.assert<ICart>(cartMinimal);
  TestValidator.equals("user_id", () => false)(minimal.user_id)(cartMinimal.user_id);
  TestValidator.predicate("should have a UUID id")(() => /^[0-9a-f\-]{36}$/.test(cartMinimal.id));
  TestValidator.predicate("should have timestamps")(() => typeof cartMinimal.created_at === "string" && typeof cartMinimal.updated_at === "string");

  // Maximal valid cart (all fields provided)
  const maximal: ICart.ICreate = {
    user_id: uuid(),
    session_id: uuid(),
  };
  const cartMaximal = await api.functional.cartOrder.cart.post(connection, { body: maximal });
  typia.assert<ICart>(cartMaximal);
  TestValidator.equals("user_id matches")(
    maximal.user_id
  )(cartMaximal.user_id);
  TestValidator.equals("session_id matches")(
    maximal.session_id
  )(cartMaximal.session_id);

  // Missing required field (user_id)
  await TestValidator.httpError("missing user_id should fail")(400)(async () =>
    // @ts-expect-error
    api.functional.cartOrder.cart.post(connection, { body: { } })
  );

  // Invalid user_id type (not a UUID)
  await TestValidator.httpError("invalid user_id type should fail")(400)(async () =>
    // @ts-expect-error
    api.functional.cartOrder.cart.post(connection, { body: { user_id: "not-a-uuid" } })
  );

  // Duplicate cart creation with same user_id (should fail with 409)
  const duplicateUserId = uuid();
  // Create the cart first
  await api.functional.cartOrder.cart.post(connection, { body: { user_id: duplicateUserId } });
  // Attempt duplicate
  await TestValidator.httpError("duplicate user_id should fail")(409)(async () =>
    api.functional.cartOrder.cart.post(connection, { body: { user_id: duplicateUserId } })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { ICart } from "@ORGANIZATION/PROJECT-api/lib/structures/ICart";

export async function test_update_cart_metadata_and_handle_invalid_deleted_case(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid cart to update
  const created: ICart = await api.functional.cartOrder.cart.post(connection, {
    body: typia.random<ICart.ICreate>(),
  });
  typia.assert<ICart>(created);

  // 2. Prepare valid update payload for cart metadata
  const updatePayload: ICart.IUpdate = {
    session_id: typia.random<string & tags.Format<"uuid">>(),
    updated_at: new Date().toISOString() as string & tags.Format<"date-time">,
  };

  // 3. Update cart metadata with valid payload
  const updated: ICart = await api.functional.cartOrder.cart.putById(connection, {
    id: created.id,
    body: updatePayload,
  });
  typia.assert<ICart>(updated);
  TestValidator.equals("cart id")<string>(created.id)(updated.id);
  if (updatePayload.session_id)
    TestValidator.equals("session_id")<string | null>(updatePayload.session_id)(updated.session_id ?? null);
  if (updatePayload.updated_at)
    TestValidator.equals("updated_at")<string>(updatePayload.updated_at)(updated.updated_at);

  // 4. Negative: Update non-existent cart id
  await TestValidator.httpError("update non-existent cart should fail")(404, 409)(async () => {
    await api.functional.cartOrder.cart.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(), // new random
      body: updatePayload,
    });
  });

  // 5. Negative: Simulate update on deleted cart (simulate by passing deleted id)
  const deletedCart: ICart = {
    ...created,
    deleted_at: new Date().toISOString() as string & tags.Format<"date-time">,
  };
  // Note: If API actually soft deletes, you'd call a delete endpoint first
  // Here, we simulate by supplying a known-to-be-deleted id (could require adjustment)
  await TestValidator.httpError("update deleted cart should fail")(404, 409, 403)(async () => {
    await api.functional.cartOrder.cart.putById(connection, {
      id: deletedCart.id,
      body: updatePayload,
    });
  });

  // 6. Negative: Attempt update with invalid data (malformed session_id)
  await TestValidator.httpError("update with invalid session_id should fail")(400)(async () => {
    await api.functional.cartOrder.cart.putById(connection, {
      id: created.id,
      body: {
        session_id: "not-a-uuid" as any,
        updated_at: "not-a-date" as any,
      },
    });
  });
}

