import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { ICartOrderCartItem } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderCartItem";
import { ICartItem } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartItem";
import { IPageICartItem } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICartItem";

export async function test_list_cart_items_with_filtering_and_empty_or_invalid_filters(
  connection: api.IConnection
): Promise<void> {
  // Preparation: Add three cart items with two separate carts and different product variants
  const cartId1 = crypto.randomUUID();
  const cartId2 = crypto.randomUUID();
  const variantId1 = crypto.randomUUID();
  const variantId2 = crypto.randomUUID();
  const variantId3 = crypto.randomUUID();

  // Create 3 cart items (2 in cart1, 1 in cart2)
  const createInputs: ICartOrderCartItem.ICreate[] = [
    { cart_id: cartId1, product_variant_id: variantId1, quantity: 2 },
    { cart_id: cartId1, product_variant_id: variantId2, quantity: 1 },
    { cart_id: cartId2, product_variant_id: variantId3, quantity: 3 },
  ];
  const created: ICartOrderCartItem[] = [];
  for (const input of createInputs) {
    const item = await api.functional.cartOrder.cartItem.post(connection, { body: input });
    typia.assert<ICartOrderCartItem>(item);
    created.push(item);
  }

  // 1. List all items (no filters)
  const listAll: IPageICartItem = await api.functional.cartOrder.cartItem.patch(connection, { body: {} });
  typia.assert<IPageICartItem>(listAll);
  const allIds = created.map((it) => it.id);
  // Each created item should be in the result set
  for (const cid of allIds) {
    TestValidator.predicate(`All list must include item #${cid}`)(() =>
      listAll.data.some((row) => row.id === cid),
    );
  }

  // 2. Filter by cart_id (cartId1)
  const byCart1: IPageICartItem = await api.functional.cartOrder.cartItem.patch(connection, { body: { cart_id: cartId1 } });
  typia.assert<IPageICartItem>(byCart1);
  const expectedCart1Ids = created.filter((i) => i.cart_id === cartId1).map((i) => i.id);
  TestValidator.equals("Filter by cart_id", undefined)(expectedCart1Ids.sort())(byCart1.data.map((i) => i.id).sort());

  // 3. Pagination test: strict limit = 2, page 1 and 2
  const paged1: IPageICartItem = await api.functional.cartOrder.cartItem.patch(connection, { body: { limit: 2, page: 1 } });
  typia.assert<IPageICartItem>(paged1);
  TestValidator.predicate("Limit = 2, returns <=2")(() => paged1.data.length <= 2);
  const paged2: IPageICartItem = await api.functional.cartOrder.cartItem.patch(connection, { body: { limit: 2, page: 2 } });
  typia.assert<IPageICartItem>(paged2);
  TestValidator.predicate("Page 2 returns valid results")(() => paged2.data.length >= 0);

  // 4a. Non-existent cart_id
  const fakeCartId = crypto.randomUUID();
  const emptyCartResult: IPageICartItem = await api.functional.cartOrder.cartItem.patch(connection, { body: { cart_id: fakeCartId } });
  typia.assert<IPageICartItem>(emptyCartResult);
  TestValidator.equals("Empty data for fake cart_id")([] as string[])(emptyCartResult.data.map((i) => i.id));

  // 4b. Malformed product_variant_id
  const badVariantResult: IPageICartItem = await api.functional.cartOrder.cartItem.patch(connection, { body: { product_variant_id: "not-a-uuid" as any } });
  typia.assert<IPageICartItem>(badVariantResult);
  TestValidator.equals("Empty for bad product_variant_id")([] as string[])(badVariantResult.data.map((i) => i.id));

  // 4c. added_before/after that exclude all (set to old timespan)
  const tooEarly = "1990-01-01T00:00:00.000Z";
  const tooLate = "2999-01-01T00:00:00.000Z";
  const allTooEarly: IPageICartItem = await api.functional.cartOrder.cartItem.patch(connection, { body: { added_before: tooEarly } });
  typia.assert<IPageICartItem>(allTooEarly);
  TestValidator.equals("Nothing before 1990")([] as string[])(allTooEarly.data.map((i) => i.id));
  const allTooLate: IPageICartItem = await api.functional.cartOrder.cartItem.patch(connection, { body: { added_after: tooLate } });
  typia.assert<IPageICartItem>(allTooLate);
  TestValidator.equals("Nothing after 2999")([] as string[])(allTooLate.data.map((i) => i.id));

  // 4d. Invalid limit value (0) -- Should be safely handled, either empty or error
  await TestValidator.error("Invalid limit=0 returns error or empty")(
    async () => {
      const r = await api.functional.cartOrder.cartItem.patch(connection, { body: { limit: 0 } });
      typia.assert<IPageICartItem>(r);
      TestValidator.equals("limit 0 = empty")([] as string[])(r.data.map((i) => i.id));
    },
  );
}

