import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { ICartOrderCartItem } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderCartItem";

/**
 * Test updating cart item data: normal, invalid, and non-existent cases.
 */
export async function test_update_cart_item_data_with_invalid_and_nonexistent_cases(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create a valid cart item to update
  const createInput: ICartOrderCartItem.ICreate = typia.random<ICartOrderCartItem.ICreate>();
  const original: ICartOrderCartItem = await api.functional.cartOrder.cartItem.post(connection, {
    body: createInput,
  });
  typia.assert<ICartOrderCartItem>(original);

  // Step 2: Valid update - increase quantity by 1
  const validQuantity = original.quantity + 1;
  const updateInput: ICartOrderCartItem.IUpdate = { quantity: validQuantity };
  const updated = await api.functional.cartOrder.cartItem.putById(connection, {
    id: original.id,
    body: updateInput,
  });
  typia.assert<ICartOrderCartItem>(updated);
  TestValidator.equals("updated quantity matches")(validQuantity)(updated.quantity);

  // Step 3: Invalid quantity (negative)
  const negativeInput: ICartOrderCartItem.IUpdate = { quantity: -3 };
  await TestValidator.httpError("negative quantity update should fail")(400, 422, 409)(async () =>
    api.functional.cartOrder.cartItem.putById(connection, {
      id: original.id,
      body: negativeInput,
    })
  );

  // Step 4: Update with malformed UUID
  await TestValidator.httpError("invalid id format should fail")(400, 422)(async () =>
    api.functional.cartOrder.cartItem.putById(connection, {
      id: "this-is-not-a-uuid" as string & tags.Format<"uuid">,
      body: { quantity: 2 },
    })
  );

  // Step 5: Update with non-existent but well-formed UUID
  const fakeUUID = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("non-existent id should give 404")(404)(async () =>
    api.functional.cartOrder.cartItem.putById(connection, {
      id: fakeUUID,
      body: { quantity: 5 },
    })
  );

  // Step 6: Update with null quantity (should likely fail or be a no-op)
  await TestValidator.httpError("null quantity should fail")(400, 422)(async () =>
    api.functional.cartOrder.cartItem.putById(connection, {
      id: original.id,
      body: { quantity: null },
    })
  );

  // Step 7: Update with empty body (no fields provided, possibly a no-op or error)
  await TestValidator.httpError("empty body update should fail")(400, 422)(async () =>
    api.functional.cartOrder.cartItem.putById(connection, {
      id: original.id,
      body: {},
    })
  );
}

import { TestValidator } from "@nestia/e2e";
