export async function test_delete_cart_item_by_id_and_handle_missing_or_deleted_case(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a cart item to guarantee there is one to delete
  const createInput: ICartOrderCartItem.ICreate = typia.random<ICartOrderCartItem.ICreate>();
  const created: ICartOrderCartItem = await api.functional.cartOrder.cartItem.post(connection, {
    body: createInput,
  });
