import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICartOrderOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderOrder";
import { IPageICartOrderOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICartOrderOrder";
import { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import typia from "typia";

export async function test_list_orders_with_various_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // Step 1: Create multiple orders with different users/carts
  const user_ids: string[] = [
    typia.random<string & typia.tags.Format<"uuid">>(),
    typia.random<string & typia.tags.Format<"uuid">>()
  ];
  const cart_ids: string[] = [
    typia.random<string & typia.tags.Format<"uuid">>(),
    typia.random<string & typia.tags.Format<"uuid">>(),
    typia.random<string & typia.tags.Format<"uuid">>()
  ];
  // We'll create 6 orders to cover users/carts/time
  const created_orders: ICartOrderOrder[] = [];
  for (let i = 0; i < 6; ++i) {
    const user_id = user_ids[i % user_ids.length];
    const cart_id = cart_ids[i % cart_ids.length];
    const created: ICartOrderOrder = await api.functional.cartOrder.order.post(connection, {
      body: {
        user_id,
        cart_id
      }
    });
    created_orders.push(created);
  }
  // Make sure we have at least 2 orders per user for filter validation
  TestValidator.predicate("at least 2 orders per user")(() =>
    created_orders.filter(o => o.user_id === user_ids[0]).length >= 2 &&
    created_orders.filter(o => o.user_id === user_ids[1]).length >= 2
  );

  // Step 2a: Filter by user_id
  for (const user_id of user_ids) {
    const result = await api.functional.cartOrder.order.patch(connection, {
      body: { user_id }
    });
    typia.assert<IPageICartOrderOrder>(result);
    TestValidator.predicate(`Only orders for user ${user_id}`)(() =>
      result.data.every(o => o.user_id === user_id)
    );
  }

  // Step 2b: Filter by cart_id
  for (const cart_id of cart_ids) {
    const result = await api.functional.cartOrder.order.patch(connection, {
      body: { cart_id }
    });
    typia.assert<IPageICartOrderOrder>(result);
    TestValidator.predicate(`Only orders for cart ${cart_id}`)(() =>
      result.data.every(o => o.cart_id === cart_id)
    );
  }

  // Step 2c: Filter by created_after/created_before window
  const timestamps = created_orders.map(o => o.created_at).sort();
  const middle_time = timestamps[Math.floor(timestamps.length/2)];
  // Orders created after 'middle_time'
  const afterResult = await api.functional.cartOrder.order.patch(connection, {
    body: { created_after: middle_time }
  });
  typia.assert<IPageICartOrderOrder>(afterResult);
  TestValidator.predicate("created_after works")(() =>
    afterResult.data.every(o => o.created_at > middle_time)
  );
  // Orders created before last timestamp
  const beforeResult = await api.functional.cartOrder.order.patch(connection, {
    body: { created_before: timestamps[timestamps.length-1] }
  });
  typia.assert<IPageICartOrderOrder>(beforeResult);
  TestValidator.predicate("created_before works")(() =>
    beforeResult.data.every(o => o.created_at < timestamps[timestamps.length-1])
  );

  // Step 2d: Pagination boundaries
  const limit = 2;
  const page1 = await api.functional.cartOrder.order.patch(connection, { body: { limit, page: 1 } });
  typia.assert<IPageICartOrderOrder>(page1);
  TestValidator.predicate("limit matches page1 count")(() =>
    page1.data.length <= limit && page1.pagination.current === 1 && page1.pagination.limit === limit
  );
  // page N (last page)
  const lastPageNum = page1.pagination.pages;
  const lastPage = await api.functional.cartOrder.order.patch(connection, { body: { limit, page: lastPageNum } });
  typia.assert<IPageICartOrderOrder>(lastPage);
  TestValidator.predicate("last page meta")(() => lastPage.pagination.current === lastPageNum);
  TestValidator.predicate("last page count not over limit")(() => lastPage.data.length <= limit);

  // Step 2e: Non-existent, invalid filter values
  // user_id that does not exist
  const invalidUser = typia.random<string & typia.tags.Format<"uuid">>();
  const invalidUserResult = await api.functional.cartOrder.order.patch(connection, { body: { user_id: invalidUser } });
  typia.assert<IPageICartOrderOrder>(invalidUserResult);
  TestValidator.equals("empty for unknown user")([] as ICartOrderOrder[])(invalidUserResult.data);
  // cart_id that does not exist
  const invalidCart = typia.random<string & typia.tags.Format<"uuid">>();
  const invalidCartResult = await api.functional.cartOrder.order.patch(connection, { body: { cart_id: invalidCart } });
  typia.assert<IPageICartOrderOrder>(invalidCartResult);
  TestValidator.equals("empty for unknown cart")([] as ICartOrderOrder[])(invalidCartResult.data);
  // impossible time window (after > before)
  const impossibleTimeResult = await api.functional.cartOrder.order.patch(connection, { body: { created_after: timestamps[timestamps.length-1], created_before: timestamps[0] } });
  typia.assert<IPageICartOrderOrder>(impossibleTimeResult);
  TestValidator.equals("empty for impossible time window")([] as ICartOrderOrder[])(impossibleTimeResult.data);
  // status that does not exist (assuming status is optional string)
  const invalidStatusResult = await api.functional.cartOrder.order.patch(connection, { body: { status: "no_such_status_et6kqp" } });
  typia.assert<IPageICartOrderOrder>(invalidStatusResult);
  TestValidator.equals("empty for unknown status")([] as ICartOrderOrder[])(invalidStatusResult.data);

  // Error handling: invalid page (e.g., 0)
  await TestValidator.httpError("page<1 returns error")(400)(async () => {
    await api.functional.cartOrder.order.patch(connection, { body: { page: 0 } });
  });
  // Error handling: invalid limit (e.g., 0)
  await TestValidator.httpError("limit<1 returns error")(400)(async () => {
    await api.functional.cartOrder.order.patch(connection, { body: { limit: 0 } });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { ICartOrderOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderOrder";

export async function test_get_order_details_by_id_and_access_control(connection: api.IConnection): Promise<void> {
  // 1. Create an order
  const createBody: ICartOrderOrder.ICreate = typia.random<ICartOrderOrder.ICreate>();
  const created: ICartOrderOrder = await api.functional.cartOrder.order.post(connection, { body: createBody });
  typia.assert<ICartOrderOrder>(created);
  TestValidator.predicate("order id exists")(() => typeof created.id === "string" && created.id.length > 0);
  TestValidator.equals("user_id matches")<string>(createBody.user_id)(created.user_id);
  TestValidator.equals("cart_id matches")<string>(createBody.cart_id)(created.cart_id);

  // 2. Retrieve the order details by id
  const read: ICartOrderOrder = await api.functional.cartOrder.order.getById(connection, { id: created.id });
  typia.assert<ICartOrderOrder>(read);
  TestValidator.equals("order fields match")(exceptTimestampsAndSoftDelete)(created)(read);

  // 3. Attempt to fetch random/invalid orderId
  const invalidId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  // Ensure we don't pick an actual valid id
  if (invalidId !== created.id) {
    await TestValidator.httpError("nonexistent order id should return 404")(404)(async () => {
      await api.functional.cartOrder.order.getById(connection, { id: invalidId });
    });
  }

  // 4. Simulated access control: simulate another user by changing the Authorization header
  if (connection.headers && connection.headers["Authorization"]) {
    const otherConnection: api.IConnection = {
      ...connection,
      headers: {
        ...connection.headers,
        Authorization: "Bearer " + typia.random<string>(), // simulate a different user
      },
    };
    await TestValidator.httpError("other user cannot access the order")(403, 404)(async () => {
      await api.functional.cartOrder.order.getById(otherConnection, { id: created.id });
    });
  }

  // 5. Optionally: (no delete API, so skip actual soft delete)
  // If deletion API becomes available, fetch after deletion and validate error.
}

function exceptTimestampsAndSoftDelete(key: string): boolean {
  // The created_at/updated_at will differ, and deleted_at can be undefined/null
  return key === "created_at" || key === "updated_at" || key === "deleted_at";
}

