/**
 * Test updating allowed order fields and enforcing immutability.
 */
export async function test_update_order_fields_with_validation_and_immutability(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid order
  const createInput: ICartOrderOrder.ICreate = typia.random<ICartOrderOrder.ICreate>();
  const order: ICartOrderOrder = await api.functional.cartOrder.order.post(connection, { body: createInput });
  typia.assert(order);

  // 2. Valid update (only allowed: updated_at)
  const updateBody: ICartOrderOrder.IUpdate = { updated_at: new Date().toISOString() as string & tags.Format<"date-time"> };
  const updatedOrder: ICartOrderOrder = await api.functional.cartOrder.order.putById(connection, { id: order.id, body: updateBody });
  typia.assert(updatedOrder);
  // Validate only updated_at has changed (other fields immutable)
  TestValidator.equals("id should be immutable")(order.id)(updatedOrder.id);
  TestValidator.equals("user_id should be immutable")(order.user_id)(updatedOrder.user_id);
  TestValidator.equals("cart_id should be immutable")(order.cart_id)(updatedOrder.cart_id);
  TestValidator.equals("updated_at should change")(updateBody.updated_at)(updatedOrder.updated_at);

  // 3. Attempt update for non-existent order (random UUID)
  const fakeId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("update non-existent order should fail")(404)(async () => {
    await api.functional.cartOrder.order.putById(connection, { id: fakeId, body: updateBody });
  });

  // 4. Attempt update for a deleted order (soft delete)
  // If possible, simulate by forcibly updating deleted_at - not directly supported, so positive test only for now
  // (If a delete API is present, use it here. Otherwise, skip.)

  // 5. Attempt improper mutation: inject extra fields
  const invalidBody = { ...updateBody, id: "hacked-id", user_id: "hacked-user", cart_id: "hacked-cart" };
  await TestValidator.httpError("should reject mutation of immutable fields")(400, 422)(async () => {
    await api.functional.cartOrder.order.putById(connection, { id: order.id, body: invalidBody as any });
  });
}

import { TestValidator } from "@nestia/e2e";
