  connection: api.IConnection,
): Promise<void> {
  // 1. Create order
  const createInput: ICartOrderOrder.ICreate = typia.random<ICartOrderOrder.ICreate>();
  const createdOrder: ICartOrderOrder = await api.functional.cartOrder.order.post(connection, { body: createInput });
  typia.assert<ICartOrderOrder>(createdOrder);

  // 2. Soft delete the order by id
  const deleteResult: ICartOrderOrderDeleteResult = await api.functional.cartOrder.order.eraseById(connection, { id: createdOrder.id });
