  // and retained in evidence logs. This is not possible in current API exposure.
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { ICartOrderOrderItem } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderOrderItem";
import { IPageICartOrderOrderItem } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICartOrderOrderItem";

/**
 * List order_item records using search and pagination filters; validate correctness.
 */
export async function test_list_order_items_with_search_and_soft_deleted_behavior(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create multiple order items for searching
  const createdItems: ICartOrderOrderItem[] = [];
  const baseOrderId = typia.random<string & typia.tags.Format<"uuid">>();
  const altOrderId = typia.random<string & typia.tags.Format<"uuid">>();
  const commonProductId = typia.random<string & typia.tags.Format<"uuid">>();
  const altProductId = typia.random<string & typia.tags.Format<"uuid">>();
  const now = new Date();
  const earlier = new Date(now.getTime() - 86400000); // 1 day ago

  // Create 3 items under baseOrderId with same product
  for (let i = 1; i <= 3; ++i) {
    const qty = 10 * i;
    const input: ICartOrderOrderItem.ICreate = {
      order_id: baseOrderId,
      product_variant_id: commonProductId,
      quantity: qty,
    };
    const output = await api.functional.cartOrder.orderItem.post(connection, { body: input });
    typia.assert(output);
    createdItems.push(output);
  }
  // Create 1 item under altOrderId, different product
  const inputAlt: ICartOrderOrderItem.ICreate = {
    order_id: altOrderId,
    product_variant_id: altProductId,
    quantity: 55,
  };
  const outputAlt = await api.functional.cartOrder.orderItem.post(connection, { body: inputAlt });
  typia.assert(outputAlt);
  createdItems.push(outputAlt);

  // Step 2: List all items (empty filter)
  const allPage = await api.functional.cartOrder.orderItem.patch(connection, {
    body: {},
  });
  typia.assert<IPageICartOrderOrderItem>(allPage);
  TestValidator.predicate("Should include all created items")(
    () => createdItems.every(item => allPage.data.find(x => x.id === item.id)),
  );

  // Step 3: Filter by order_id
  const byOrderId = await api.functional.cartOrder.orderItem.patch(connection, {
    body: { order_id: baseOrderId },
  });
  typia.assert<IPageICartOrderOrderItem>(byOrderId);
  TestValidator.predicate("All items should be under baseOrderId")(
    () => byOrderId.data.every(x => x.order_id === baseOrderId),
  );

  // Step 4: Filter by product_variant_id
  const byProductId = await api.functional.cartOrder.orderItem.patch(connection, {
