  );

  // Step 6: Filter by added_at_range
  const recent = await api.functional.cartOrder.orderItem.patch(connection, {
    body: { added_at_range: [earlier.toISOString(), now.toISOString()] },
  });
  typia.assert<IPageICartOrderOrderItem>(recent);
  TestValidator.predicate("added_at within date range")(
    () => recent.data.every(x => {
      const at = new Date(x.added_at).getTime();
      return at >= earlier.getTime() && at <= now.getTime();
    }),
  );

  // Step 7: Invalid/malformed filters (invalid UUIDs)
  await TestValidator.httpError("Invalid order_id format")(400)(async () => {
    await api.functional.cartOrder.orderItem.patch(connection, {
      body: { order_id: "not-a-uuid" as any },
    });
  });
  await TestValidator.httpError("Malformed quantity range")(400)(async () => {
    await api.functional.cartOrder.orderItem.patch(connection, {
      body: { quantity_range: [100] as any }, // not [min,max]
    });
  });
  await TestValidator.httpError("Added_at reversed")(400)(async () => {
    await api.functional.cartOrder.orderItem.patch(connection, {
      body: { added_at_range: [now.toISOString(), earlier.toISOString()] },
    });
  });

  // Step 8: Soft-delete check (assume N/A; if soft-delete present and API exposes flag, test visibility)
  // -- This part is skipped/not implemented as API/DTO exposes no soft-delete/include_deleted parameter.
}

