import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICartOrderOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderOrder";
import { ICartOrderOrderItem } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderOrderItem";
import typia from "typia";

function randomUUID(): string {
  // Simple random UUID v4 generator for tests
  return "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g, c => {
    const r = Math.random() * 16 | 0,
      v = c === "x" ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

export async function test_create_order_item_with_valid_invalid_and_duplicate_constraints(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create order (dependency)
  const orderInput: ICartOrderOrder.ICreate = {
    user_id: randomUUID(),
    cart_id: randomUUID(),
  };
  const order = await api.functional.cartOrder.order.post(connection, {
    body: orderInput,
  });
  typia.assert<ICartOrderOrder>(order);

  // Valid, random UUID for product_variant
  const product_variant_id = randomUUID();

  // 3. Happy Path: valid order item
  const itemInput: ICartOrderOrderItem.ICreate = {
    order_id: order.id,
    product_variant_id,
    quantity: 2,
  };
  const item = await api.functional.cartOrder.orderItem.post(connection, { body: itemInput });
  typia.assert<ICartOrderOrderItem>(item);
  TestValidator.equals("order_id")(order.id)(item.order_id);
  TestValidator.equals("product_variant_id")(product_variant_id)(item.product_variant_id);

  // 4. Missing required fields
  await TestValidator.httpError("missing order_id")(400)(async () =>
    api.functional.cartOrder.orderItem.post(connection, { body: { product_variant_id, quantity: 1 } as any })
  );
  await TestValidator.httpError("missing product_variant_id")(400)(async () =>
    api.functional.cartOrder.orderItem.post(connection, { body: { order_id: order.id, quantity: 1 } as any })
  );
  await TestValidator.httpError("missing quantity")(400)(async () =>
    api.functional.cartOrder.orderItem.post(connection, { body: { order_id: order.id, product_variant_id } as any })
  );

  // 5. Invalid references (random order_id)
  await TestValidator.httpError("invalid order_id")(404, 400)(async () =>
    api.functional.cartOrder.orderItem.post(connection, {
      body: { order_id: randomUUID(), product_variant_id, quantity: 1 },
    })
  );
  await TestValidator.httpError("invalid product_variant_id")(404, 400)(async () =>
    api.functional.cartOrder.orderItem.post(connection, {
      body: { order_id: order.id, product_variant_id: randomUUID(), quantity: 1 },
    })
  );

  // 6. Duplicate constraints
  await TestValidator.httpError("duplicate order_item")(409)(async () =>
    api.functional.cartOrder.orderItem.post(connection, { body: itemInput })
  );

  // 7. Quantity rules (zero, negative, very large)
  for (const q of [0, -1, 999999999]) {
    await TestValidator.httpError(`invalid quantity: ${q}`)(400)(async () =>
      api.functional.cartOrder.orderItem.post(connection, {
        body: { order_id: order.id, product_variant_id, quantity: q },
      })
    );
  }
}

