import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IOrderStatus } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderStatus";
import { IPageIOrderStatus } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIOrderStatus";

/**
 * E2E test for listing and searching CartOrder order_status with pagination and filtering.
 * Covers valid filter, empty filter, and invalid filter cases. Validates correct result and typing.
 */
export async function test_list_order_status_with_pagination_and_various_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create several order_status records with different values
  const base_time = new Date();
  const orders = [
    typia.random<IOrderStatus.ICreate>(),
    typia.random<IOrderStatus.ICreate>(),
    typia.random<IOrderStatus.ICreate>(),
  ];

  // Assign the same order_id for two records and unique for third to test order_id filtering
  const shared_order_id = typia.random<string & typia.tags.Format<"uuid">>();
  orders[0].order_id = shared_order_id;
  orders[1].order_id = shared_order_id;
  // make sure status_code and changed_at are distinct for filter/sorting tests
  orders[0].status_code = "pending";
  orders[0].changed_at = new Date(base_time.getTime() - 10000).toISOString();
  orders[1].status_code = "shipped";
  orders[1].changed_at = new Date(base_time.getTime() - 5000).toISOString();
  orders[2].status_code = "cancelled";
  orders[2].changed_at = base_time.toISOString();

  // create all
  const created: IOrderStatus[] = [];
  for (const input of orders) {
    const output = await api.functional.cartOrder.orderStatus.post(connection, { body: input });
    typia.assert<IOrderStatus>(output);
    created.push(output);
  }

  // 2. List all with empty filter
  const allList = await api.functional.cartOrder.orderStatus.patch(connection, { body: {} });
  typia.assert<IPageIOrderStatus>(allList);
  TestValidator.predicate("should return at least 3 records")(() => allList.data.length >= 3);

  // 3. Filter by order_id (should yield 2)
  const orderIdFilterResult = await api.functional.cartOrder.orderStatus.patch(connection, { body: { order_id: shared_order_id } });
  typia.assert<IPageIOrderStatus>(orderIdFilterResult);
  TestValidator.predicate("filter by order_id works")(() => orderIdFilterResult.data.length === 2);
  for (const rec of orderIdFilterResult.data) {
    TestValidator.equals("shared order_id matches")(shared_order_id)(rec.order_id);
  }

  // 4. Filter by status_code ("cancelled")
  const statusCodeResult = await api.functional.cartOrder.orderStatus.patch(connection, { body: { status_code: "cancelled" } });
  typia.assert<IPageIOrderStatus>(statusCodeResult);
  TestValidator.predicate("filter by status_code returns 1")(() => statusCodeResult.data.length === 1);
  TestValidator.equals("cancelled status_code")("cancelled")(statusCodeResult.data[0]?.status_code);

  // 5. Filter by changed_at_range
  const from = orders[1].changed_at;
  const to = orders[2].changed_at;
  const changedAtRangeResult = await api.functional.cartOrder.orderStatus.patch(connection, { body: { changed_at_range: [from, to] }});
  typia.assert<IPageIOrderStatus>(changedAtRangeResult);
  TestValidator.predicate("changed_at_range yields >=2 records")(() => changedAtRangeResult.data.length >= 2);
  for (const rec of changedAtRangeResult.data) {
    TestValidator.predicate("changed_at in range")( () => rec.changed_at >= from && rec.changed_at <= to );
  }

  // 6. Invalid filter (nonsense order_id)
  const nonsenseId = "00000000-0000-0000-0000-000000000000";
  const invalidResult = await api.functional.cartOrder.orderStatus.patch(connection, { body: { order_id: nonsenseId } });
  typia.assert<IPageIOrderStatus>(invalidResult);
  TestValidator.equals("invalid filter yields empty")( [] as IPageIOrderStatus["data"] )(invalidResult.data);

  // 7. Sorting check (changed_at ascending)
  // Not strictly required by contract, but we test that records are sorted by changed_at ascending, if so.
  const sorted = [...allList.data].sort((a, b) => a.changed_at.localeCompare(b.changed_at));
  TestValidator.equals("sorted by changed_at")(sorted)(allList.data);

  // 8. (Optional) Invalid date range format (1 date instead of 2) => error or empty
  await TestValidator.error("invalid changed_at_range array")(
    () => api.functional.cartOrder.orderStatus.patch(connection, { body: { changed_at_range: [from] as any } })
  );
}

