): Promise<void> {
  // 1. Create a baseline personalization event
  const createInput: IFavoritesPersonalization.IPersonalizationEventCreate = typia.random<IFavoritesPersonalization.IPersonalizationEventCreate>();
  const created: IFavoritesPersonalization.IPersonalizationEvent = await api.functional.favoritesPersonalization.personalizationEvents.post(connection, { body: createInput });
  typia.assert(created);

  // 2. No filter: Should return at least the created event
  const allResp = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, { body: {} });
  typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(allResp);
  TestValidator.predicate("no filter - at least 1 event")(() => allResp.data.length >= 1);
  TestValidator.predicate("no filter - pagination valid")(() => allResp.pagination.current >= 1 && allResp.pagination.limit >= 1 && allResp.pagination.records >= 1 && allResp.pagination.pages >= 1);

  // For convenience
  const eqEvent = TestValidator.equals("event match");
  // 3. Filter by user_id
  const byUser = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, { body: { user_id: created.user_id } });
  typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(byUser);
  TestValidator.predicate("user filter")(() => byUser.data.every(ev => ev.user_id === created.user_id));

  // 4. Filter by event_type
