  typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(byType);
  TestValidator.predicate("event_type filter")(() => byType.data.every(ev => ev.event_type === created.event_type));

  // 5. Filter by date range containing the event
  const ts = created.timestamp;
  // Use a window that definitely includes the event
  const byDateIn = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, { body: { timestamp_from: ts, timestamp_to: ts } });
  typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(byDateIn);
  TestValidator.predicate("date-in filter")(() => byDateIn.data.some(ev => ev.id === created.id));

  // 6. Filter by date range outside the event
  // Use an impossible range before the event
  const byDateOut = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, { body: { timestamp_from: "1900-01-01T00:00:00.000Z", timestamp_to: "1900-01-02T00:00:00.000Z" } });
  typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(byDateOut);
  TestValidator.equals("empty data array")<IFavoritesPersonalization.IPersonalizationEvent[]>([])(byDateOut.data);
  TestValidator.predicate("empty pagination valid")(() => byDateOut.pagination.current >= 1 && byDateOut.pagination.limit >= 1);

  // 7. Pagination test (limit=1, page=1 and page=2)
  const paged1 = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, { body: { limit: 1, page: 1 } });
  typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(paged1);
  TestValidator.equals("pagination limit 1")<number>(1)(paged1.data.length);
  TestValidator.predicate("pagination meta valid")(() => paged1.pagination.limit === 1 && paged1.pagination.current === 1 && paged1.pagination.pages >= 1);

  // Page 2 (may be empty if there is only one event)
  const paged2 = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, { body: { limit: 1, page: 2 } });
  typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(paged2);
  TestValidator.predicate("pagination page2 meta valid")(() => paged2.pagination.limit === 1 && paged2.pagination.current === 2 && paged2.pagination.pages >= 1);
  // Can't guarantee there are items
}

