import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";
import { IPersonalizationEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPersonalizationEvent";

export async function test_update_personalization_event_by_id_with_valid_and_invalid_conditions(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a personalization event (POST)
  const created: IFavoritesPersonalization.IPersonalizationEvent =
    await api.functional.favoritesPersonalization.personalizationEvents.post(
      connection,
      {
        body: typia.random<IFavoritesPersonalization.IPersonalizationEventCreate>(),
      },
    );
  typia.assert(created);

  // 2. Update it using PUT (valid case)
  const updateInput: IPersonalizationEvent.IUpdate = {
    // Change classifiable/updatable fields only; keep user_id/id static
    event_type: "click",
    model_version: "v2-updated-test",
    context: "test-device-changed",
    // Change timestamp (valid ISO string in RFC3339)
    timestamp: new Date(Date.now() + 10000).toISOString() as string & tags.Format<"date-time">,
    product_id: created.product_id,
    recommendation_id: created.recommendation_id,
  };
  const updated: IPersonalizationEvent =
    await api.functional.favoritesPersonalization.personalizationEvents.putById(
      connection,
      {
        id: created.id,
        body: updateInput,
      },
    );
  typia.assert(updated);
  // 3. Validate update: id, user_id unchanged; others updated as expected
  TestValidator.equals("id matches")(created.id)(updated.id);
  TestValidator.equals("user_id matches")(created.user_id)(updated.user_id);
  TestValidator.equals("event_type updated")("click")(updated.event_type);
  TestValidator.equals("model_version updated")("v2-updated-test")(updated.model_version);
  TestValidator.equals("context updated")("test-device-changed")(updated.context);
  TestValidator.equals("product_id unchanged")(created.product_id)(updated.product_id);
  TestValidator.equals("recommendation_id unchanged")(created.recommendation_id)(updated.recommendation_id);

  // 4. Attempt update with invalid/non-existent ID
  const invalidId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("update with non-existent ID should fail")(404)(async () =>
    api.functional.favoritesPersonalization.personalizationEvents.putById(
      connection,
      {
        id: invalidId,
        body: updateInput,
      },
    ),
  );

  // 5. Attempt update with invalid data (missing required field)
  // Remove required field: event_type
  const invalidUpdate: Partial<IPersonalizationEvent.IUpdate> = {
    // Deliberately omit event_type
    model_version: "fail-case",
    context: null,
    timestamp: "2026-12-30T00:00:00Z" as string & tags.Format<"date-time">,
    product_id: created.product_id,
    recommendation_id: created.recommendation_id,
  };
  // @ts-expect-error - Intentionally passing wrong type
  await TestValidator.error("missing required field should throw error")(() =>
    api.functional.favoritesPersonalization.personalizationEvents.putById(
      connection,
      {
        id: created.id,
        body: invalidUpdate as any,
      },
    ),
  );

  // 6. Attempt update with invalid data (invalid product_id type)
  const badUpdate: IPersonalizationEvent.IUpdate = {
    event_type: "save",
    model_version: "fail-case-2",
    context: null,
    timestamp: new Date().toISOString() as string & tags.Format<"date-time">,
    // @ts-expect-error
    product_id: "not-a-uuid" as any,
    recommendation_id: created.recommendation_id,
  };
  // @ts-expect-error
  await TestValidator.error("invalid product_id should throw error")(() =>
    api.functional.favoritesPersonalization.personalizationEvents.putById(
      connection,
      {
        id: created.id,
        body: badUpdate,
      },
    ),
  );
}

