import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IRecommendation } from "@ORGANIZATION/PROJECT-api/lib/structures/IRecommendation";
import { IPageIRecommendation } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIRecommendation";
import typia from "typia";

export async function test_search_and_paginate_recommendations_with_filters(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create multiple distinct recommendations
  const userA = crypto.randomUUID();
  const userB = crypto.randomUUID();
  const productX = crypto.randomUUID();
  const productY = crypto.randomUUID();
  const now = new Date().toISOString();

  // Recommendation 1: userA, productX, model1
  const rec1Input: IRecommendation.ICreate = {
    user_id: userA,
    product_id: productX,
    model_id: "model1",
    model_version: "v1.0",
    rationale: "Top pick for userA by model1.",
    recommendation_time: now,
  };
  const rec1 = await api.functional.favoritesPersonalization.recommendations.post(connection, { body: rec1Input });

  // Recommendation 2: userA, productY, model2
  const rec2Input: IRecommendation.ICreate = {
    user_id: userA,
    product_id: productY,
    model_id: "model2",
    model_version: "v2.0",
    rationale: "Top pick for userA by model2.",
    recommendation_time: now,
  };
  const rec2 = await api.functional.favoritesPersonalization.recommendations.post(connection, { body: rec2Input });

  // Recommendation 3: userB, productY, model1
  const rec3Input: IRecommendation.ICreate = {
    user_id: userB,
    product_id: productY,
    model_id: "model1",
    model_version: "v1.0",
    rationale: "Top pick for userB by model1.",
    recommendation_time: now,
  };
  const rec3 = await api.functional.favoritesPersonalization.recommendations.post(connection, { body: rec3Input });

  // Step 2: Retrieve recommendations for userA
  const userAResult = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: { user_id: userA },
  });
  typia.assert<IPageIRecommendation>(userAResult);
  TestValidator.predicate("only userA's recommendations")(
    userAResult.data.every(rec => rec.user_id === userA)
  );

  // Step 3: Retrieve by product_id (productY)
  const productYResult = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: { product_id: productY },
  });
  typia.assert<IPageIRecommendation>(productYResult);
  TestValidator.predicate("only productY recommendations")(
    productYResult.data.length >= 1 && productYResult.data.every(rec => rec.product_id === productY)
  );

  // Step 4: Combined filter (userA, model2)
  const userAmodel2Result = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: { user_id: userA, model_id: "model2" },
  });
  typia.assert<IPageIRecommendation>(userAmodel2Result);
  TestValidator.equals("filter userA + model2")(
    [rec2]
  )(userAmodel2Result.data);

  // Step 5: Pagination test (limit=1, offset=0/1)
  const paged0 = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: { limit: 1, offset: 0 },
  });
  typia.assert<IPageIRecommendation>(paged0);
  TestValidator.predicate("1st page has 1 item")(
    paged0.data.length === 1
  );
  const paged1 = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: { limit: 1, offset: 1 },
  });
  typia.assert<IPageIRecommendation>(paged1);
  // Ensure page 1's item is not the same as page 2's item (if possible)
  if (paged1.data.length === 1 && paged0.data.length === 1) {
    TestValidator.predicate("pagination distinct items")(
      paged0.data[0].id !== paged1.data[0].id
    );
  }

  // Step 6: Empty result test (non-existent user_id)
  const emptyResult = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    body: { user_id: crypto.randomUUID() },
  });
  typia.assert<IPageIRecommendation>(emptyResult);
  TestValidator.equals("empty result")([] as IRecommendation[])(emptyResult.data);
}

