import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { IRecommendation } from "@ORGANIZATION/PROJECT-api/lib/structures/IRecommendation";

export async function test_update_recommendation_by_id_and_invalid_fields(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a recommendation record for update testing
  const createInput: IRecommendation.ICreate = typia.random<IRecommendation.ICreate>();
  const original: IRecommendation = await api.functional.favoritesPersonalization.recommendations.post(
    connection,
    { body: createInput },
  );
  typia.assert(original);

  // 2. Prepare a valid update body with permitted fields
  const updateBody: IRecommendation.IUpdate = {
    model_version: original.model_version + "-updated",
    rationale: "Updated rationale for testing.",
    recommendation_time: new Date().toISOString() as string & tags.Format<"date-time">,
  };

  // 3. Update the record with valid ID
  const updated: IRecommendation = await api.functional.favoritesPersonalization.recommendations.putById(
    connection,
    { id: original.id, body: updateBody },
  );
  typia.assert(updated);

  // 4. Confirm the changes are reflected
  TestValidator.equals("model_version should be updated")<string>(updateBody.model_version!)(updated.model_version);
  TestValidator.equals("rationale should be updated")<string | null>(updateBody.rationale!)(updated.rationale);
  TestValidator.equals("recommendation_time should be updated")<string>(updateBody.recommendation_time!)(updated.recommendation_time);
  // Fields that should not change
  TestValidator.equals("user_id should be unchanged")<string>(original.user_id)(updated.user_id);
  TestValidator.equals("product_id should be unchanged")<string>(original.product_id)(updated.product_id);
  TestValidator.equals("model_id should be unchanged")<string>(original.model_id)(updated.model_id);
  TestValidator.equals("id should be unchanged")<string>(original.id)(updated.id);

  // 5. Negative test: invalid (random) recommendation ID
  const invalidId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("Should error for invalid recommendation ID")(404, 400)(async () => {
    await api.functional.favoritesPersonalization.recommendations.putById(
      connection,
      { id: invalidId, body: updateBody },
    );
  });

  // 6. Negative test: forbidden fields in update (should trigger validation error)
  // Compose an update body with forbidden fields. Technically, TS should catch this, but simulate an input extension.
  const forbiddenUpdate = {
    ...updateBody,
    product_id: original.product_id,
    user_id: original.user_id,
    model_id: original.model_id,
  } as any;

  await TestValidator.httpError("Should error for forbidden fields in update")(400, 422)(async () => {
    await api.functional.favoritesPersonalization.recommendations.putById(
      connection,
      { id: original.id, body: forbiddenUpdate },
    );
  });
}

