export async function test_soft_delete_recommendation_by_id_and_handle_nonexistent_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create new recommendation
  const input: IRecommendation.ICreate = createValidRecommendation();
  const recommendation = await api.functional.favoritesPersonalization.recommendations.post(
    connection,
    { body: input },
  );
  typia.assert<IRecommendation>(recommendation);

  // 2. Soft-delete the created recommendation
  const deleted = await api.functional.favoritesPersonalization.recommendations.eraseById(
    connection,
    { id: recommendation.id },
  );
  typia.assert<IRecommendation>(deleted);
  TestValidator.equals("deleted record ID matches")<string>(recommendation.id)(deleted.id);

  // 3. Re-delete the same already deleted recommendation (should be idempotent or error)
  await TestValidator.error("double delete should error or return idempotent")(
    async () => {
      await api.functional.favoritesPersonalization.recommendations.eraseById(
        connection,
        { id: recommendation.id },
      );
    },
  );

  // 4. Try deleting a completely non-existent UUID (valid format)
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  // Ensure it's not the same as previously created
  if (nonExistentId === recommendation.id) {
    throw new Error("Random UUID collision; retry test");
  }
  await TestValidator.error("delete non-existent id should fail")(
    async () => {
      await api.functional.favoritesPersonalization.recommendations.eraseById(
        connection,
        { id: nonExistentId },
      );
    },
  );

  // 5. Try deleting with an invalid UUID (bad format)
  const invalidId = "not-a-uuid-format" as string & tags.Format<"uuid">;
  await TestValidator.error("delete with invalid UUID should fail")(
    async () => {
      await api.functional.favoritesPersonalization.recommendations.eraseById(
        connection,
        { id: invalidId },
      );
    },
  );
}

