export async function test_search_and_paginate_personalization_audits_with_filters(connection: api.IConnection): Promise<void> {
  // 1. Create multiple audits with differing fields
  const baseTimestamp = new Date().toISOString();
  const audits: IPersonalizationAudit[] = [];

  const createInput1: IPersonalizationAudit.ICreate = {
    user_id: typia.random<string & typia.tags.Format<"uuid">>(),
    favorite_id: null,
    bookmark_id: null,
    recommendation_id: null,
    personalization_event_id: null,
    action_type: "ACTION_TYPE_A",
    evidence_hash: "abc123",
    timestamp: baseTimestamp,
  };
  const audit1 = await api.functional.favoritesPersonalization.personalizationAudits.post(connection, { body: createInput1 });
  audits.push(audit1);

  const createInput2: IPersonalizationAudit.ICreate = {
    user_id: typia.random<string & typia.tags.Format<"uuid">>(),
    favorite_id: typia.random<string & typia.tags.Format<"uuid">>(),
    bookmark_id: null,
    recommendation_id: null,
    personalization_event_id: null,
    action_type: "ACTION_TYPE_B",
    evidence_hash: "def456",
    timestamp: new Date(Date.now() - 1000 * 60).toISOString(), // 1 min earlier
  };
  const audit2 = await api.functional.favoritesPersonalization.personalizationAudits.post(connection, { body: createInput2 });
  audits.push(audit2);

  // 2. Search by user_id (should return correct audit)
  const resByUser: IPageIPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { user_id: audit1.user_id },
  });
  typia.assert(resByUser);
  TestValidator.predicate("Search by user_id finds the correct audit")(resByUser.data.some(r => r.id === audit1.id));

  // 3. Search by action_type
  const resByAction: IPageIPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { action_type: "ACTION_TYPE_B" },
  });
  typia.assert(resByAction);
  TestValidator.predicate("Search by action_type finds correct audit")(
    resByAction.data.some(r => r.id === audit2.id)
  );

  // 4. Combined search (user_id and action_type that should only match audit1)
  const resCombined: IPageIPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { user_id: audit1.user_id, action_type: audit1.action_type },
  });
  typia.assert(resCombined);
  TestValidator.predicate("Combined filter returns only audit1")(
    resCombined.data.every(a => a.user_id === audit1.user_id && a.action_type === audit1.action_type)
  );

  // 5. Search with non-existent evidence_hash yields empty result
  const resNone: IPageIPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { evidence_hash: "nonexistenthashXYZ" },
  });
  typia.assert(resNone);
  TestValidator.equals("Empty result for nonexistent evidence_hash")([] as IPersonalizationAudit[])(resNone.data);
  TestValidator.predicate("Pagination still valid for empty dataset")(
    resNone.pagination && typeof resNone.pagination.current === "number"
  );

  // 6. Pagination test: limit=1
  const resPaging: IPageIPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: { limit: 1, offset: 0 },
  });
  typia.assert(resPaging);
  TestValidator.predicate("Page size is 1 when limited")(
    resPaging.data.length === 1 || resPaging.data.length === 0 // if dataset is too small
  );
  TestValidator.predicate("Pagination info correct")(
    typeof resPaging.pagination.current === "number" && typeof resPaging.pagination.limit === "number"
  );

  // 7. Validate ordering by timestamp desc (if API returns ordered)
  const resAll: IPageIPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, {
    body: {}, // no filters, get whatever is present
  });
  typia.assert(resAll);
  const timestamps = resAll.data.map(a => new Date(a.timestamp).getTime());
  const sorted = [...timestamps].sort((a, b) => b - a);
  TestValidator.equals("Audits sorted by timestamp desc")(sorted)(timestamps);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { IPersonalizationAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPersonalizationAudit";

export async function test_get_personalization_audit_by_id_with_valid_and_invalid_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Positive: create via POST, retrieve via GET, check matches
  const createInput: IPersonalizationAudit.ICreate = {
    user_id: typia.random<string & tags.Format<"uuid">>(),
    action_type: "ACTIONS",
    evidence_hash: "test_hash_" + Math.random().toString(36).slice(2, 8),
    timestamp: new Date().toISOString(),
    // Optionals as undefined: ensures minimal valid object
    favorite_id: undefined,
    bookmark_id: undefined,
    recommendation_id: undefined,
    personalization_event_id: undefined,
  };
  // POST create
  const created: IPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.post(
    connection,
    { body: createInput },
  );
  typia.assert<IPersonalizationAudit>(created);
  // GET by id
  const gotten: IPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.getById(
    connection,
    { id: created.id },
  );
  typia.assert<IPersonalizationAudit>(gotten);

  // Sanity: field checks
  TestValidator.equals("user_id")(createInput.user_id)(gotten.user_id);
  TestValidator.equals("action_type")(createInput.action_type)(gotten.action_type);
  TestValidator.equals("evidence_hash")(createInput.evidence_hash)(gotten.evidence_hash);
  TestValidator.equals("timestamp")(createInput.timestamp)(gotten.timestamp);
  TestValidator.equals("favorite_id")(createInput.favorite_id ?? null)(gotten.favorite_id ?? null);
  TestValidator.equals("bookmark_id")(createInput.bookmark_id ?? null)(gotten.bookmark_id ?? null);
  TestValidator.equals("recommendation_id")(createInput.recommendation_id ?? null)(gotten.recommendation_id ?? null);
  TestValidator.equals("personalization_event_id")(createInput.personalization_event_id ?? null)(gotten.personalization_event_id ?? null);

  // 2. Negative: GET with random (non-existent) id should 404/error
  const randomId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  // Defensive: don't accidentally collide with real id
  if (randomId !== created.id) {
    await TestValidator.httpError("should fail for non-existent id")(404)(() =>
      api.functional.favoritesPersonalization.personalizationAudits.getById(
        connection,
        { id: randomId },
      ),
    );
  }
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPersonalizationAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPersonalizationAudit";
import typia from "typia";

/**
 * E2E test: Create personalization audit (valid & invalid input)
 */
export async function test_create_personalization_audit_with_valid_and_invalid_input(
  connection: api.IConnection,
): Promise<void> {
  // --- Positive case ---
  const validInput: IPersonalizationAudit.ICreate = {
    user_id: "11111111-1111-1111-1111-111111111111",
    action_type: "ACTIONS",
    evidence_hash: "deadbeefcafebabe0123456789abcdef01234567",
    timestamp: "2023-06-24T04:41:05.442Z",
    // optional fields
    favorite_id: "22222222-2222-2222-2222-222222222222",
    bookmark_id: "33333333-3333-3333-3333-333333333333",
    recommendation_id: "44444444-4444-4444-4444-444444444444",
    personalization_event_id: "55555555-5555-5555-5555-555555555555",
  };

  const created = await api.functional.favoritesPersonalization.personalizationAudits.post(
    connection,
    { body: validInput },
  );
  typia.assert<IPersonalizationAudit>(created);
  TestValidator.equals("audit user_id")(validInput.user_id)(created.user_id);
  TestValidator.equals("audit action_type")(validInput.action_type)(created.action_type);
  TestValidator.equals("audit evidence_hash")(validInput.evidence_hash)(created.evidence_hash);
  TestValidator.equals("audit timestamp")(validInput.timestamp)(created.timestamp);
  TestValidator.equals("audit favorite_id")(validInput.favorite_id)(created.favorite_id);
  TestValidator.equals("audit bookmark_id")(validInput.bookmark_id)(created.bookmark_id);
  TestValidator.equals("audit recommendation_id")(validInput.recommendation_id)(created.recommendation_id);
  TestValidator.equals("audit personalization_event_id")(validInput.personalization_event_id)(created.personalization_event_id);
  TestValidator.predicate("audit id should be a UUID")(() => typeof created.id === "string" && created.id.length > 0);

  // --- Negative cases: missing and invalid fields ---
  // Helper: shallow copy and remove key
  const without = <T extends object, K extends keyof T>(obj: T, ...keys: K[]): T => {
    const clone = { ...obj };
    for (const k of keys) delete clone[k];
    return clone;
  };

  // 1. Missing user_id
  await TestValidator.httpError("missing user_id")(400)(async () => {
    await api.functional.favoritesPersonalization.personalizationAudits.post(
      connection,
      { body: without(validInput, "user_id") },
    );
  });

  // 2. Missing action_type
  await TestValidator.httpError("missing action_type")(400)(async () => {
    await api.functional.favoritesPersonalization.personalizationAudits.post(
      connection,
      { body: without(validInput, "action_type") },
    );
  });

  // 3. Missing evidence_hash
  await TestValidator.httpError("missing evidence_hash")(400)(async () => {
    await api.functional.favoritesPersonalization.personalizationAudits.post(
      connection,
      { body: without(validInput, "evidence_hash") },
    );
  });

  // 4. Missing timestamp
  await TestValidator.httpError("missing timestamp")(400)(async () => {
    await api.functional.favoritesPersonalization.personalizationAudits.post(
      connection,
      { body: without(validInput, "timestamp") },
    );
  });

  // 5. Invalid UUID for user_id
  const invalidUuidInput = { ...validInput, user_id: "not-a-uuid" };
  await TestValidator.httpError("invalid UUID user_id")(400)(async () => {
    await api.functional.favoritesPersonalization.personalizationAudits.post(
      connection,
      { body: invalidUuidInput },
    );
  });

  // 6. Invalid timestamp
  const invalidTimestampInput = { ...validInput, timestamp: "not-a-date" };
  await TestValidator.httpError("invalid timestamp")(400)(async () => {
    await api.functional.favoritesPersonalization.personalizationAudits.post(
      connection,
      { body: invalidTimestampInput },
    );
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IPersonalizationAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPersonalizationAudit";

export async function test_update_personalization_audit_by_id_and_invalid_update_attempts(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid audit record
  const createInput: IPersonalizationAudit.ICreate = typia.random<IPersonalizationAudit.ICreate>();
  const audit: IPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.post(connection, { body: createInput });
  typia.assert<IPersonalizationAudit>(audit);

  // 2. Update the record by allowed fields only
  const updateInput: IPersonalizationAudit.IUpdate = {
    action_type: "UPDATED_ACTION",
    evidence_hash: "newEvidenceHashValue_1234567890",
    timestamp: new Date().toISOString() as string & tags.Format<"date-time">,
  };

  const updated: IPersonalizationAudit = await api.functional.favoritesPersonalization.personalizationAudits.putById(connection, {
    id: audit.id,
    body: updateInput,
  });
  typia.assert<IPersonalizationAudit>(updated);
  // The updated response should match updated values, except unchanged fields
  TestValidator.equals("Updated action_type")<string>(updateInput.action_type!)(updated.action_type);
  TestValidator.equals("Updated evidence_hash")<string>(updateInput.evidence_hash!)(updated.evidence_hash);
  TestValidator.equals("Updated timestamp")<string>(updateInput.timestamp!)(updated.timestamp);

  // 3. Attempt update with restricted/extra field: purposely send extra field (should be filtered/type error)
  await TestValidator.error("Put with disallowed extra field should fail")(() =>
    api.functional.favoritesPersonalization.personalizationAudits.putById(
      connection as any,
      {
        id: audit.id,
        // Intentionally cast body to 'any' to bypass TS compile and trigger at runtime
        body: {
          action_type: "ILLEGAL",
          evidence_hash: "illegalhash",
          illegal_field: "this_should_not_be_accepted",
        } as any,
      },
    )
  );

  // 4. Attempt update with bad data (invalid evidence_hash and timestamp type/format)
  await TestValidator.error("Put with invalid evidence_hash and timestamp types should fail")(() =>
    api.functional.favoritesPersonalization.personalizationAudits.putById(
      connection,
      {
        id: audit.id,
        body: {
          action_type: "BAD_DATA_TEST",
          evidence_hash: 123456 as any, // not string
          timestamp: "not-a-datetime" as any, // not a valid ISO string
        },
      }
    )
  );

  // 5. Attempt update with a random, non-existent ID
  const randomId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("Put on non-existent audit should 404")(404)(() =>
    api.functional.favoritesPersonalization.personalizationAudits.putById(
      connection,
      {
        id: randomId,
        body: updateInput,
      },
    )
  );

  // 6. Original persisted record should remain correct after failed attempts (cannot fully assert external backend state)
  // Typically another GET would verify, but not implemented in provided API spec.
}

