import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IAiModel } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiModel";
import { IPageIAiModel } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiModel";

export async function test_list_and_filter_ai_models_with_pagination(connection: api.IConnection): Promise<void> {
  // 1. Create a new AI Model for test
  const newModelInput: IAiModel.ICreate = {
    organization_id: typia.random<string>(),
    owner_id: typia.random<string>(),
    name: `test-model-${Math.random().toString(36).substring(2, 10)}`,
    model_type: "test-filter-type",
    description: "Model for E2E search and filter tests"
  };

  const createdModel: IAiModel = await api.functional.advancedAI.aiModels.post(connection, { body: newModelInput });
  typia.assert(createdModel);
  TestValidator.equals("Created model name")(newModelInput.name)(createdModel.name);
  TestValidator.equals("Created model type")(newModelInput.model_type)(createdModel.model_type);
  TestValidator.equals("Created model organization")(newModelInput.organization_id)(createdModel.organization_id);
  TestValidator.equals("Created model owner")(newModelInput.owner_id)(createdModel.owner_id);

  // 2. Default: List all (should include the created model)
  const allList: IPageIAiModel = await api.functional.advancedAI.aiModels.patch(connection, { body: {} });
  typia.assert(allList);
  // Assert at least the created model exists
  TestValidator.predicate("Created model exists in all listing")(() => allList.data.some(m => m.id === createdModel.id));

  // 3. Filter: By model_type
  const byType: IPageIAiModel = await api.functional.advancedAI.aiModels.patch(connection, {
    body: { model_type: createdModel.model_type }
  });
  typia.assert(byType);
  TestValidator.predicate("All results have correct model_type")(() => byType.data.every(m => m.model_type === createdModel.model_type));
  TestValidator.predicate("Result includes created model by type")(() => byType.data.some(m => m.id === createdModel.id));

  // 4. Filter: By owner_id
  const byOwner: IPageIAiModel = await api.functional.advancedAI.aiModels.patch(connection, {
    body: { owner_id: createdModel.owner_id }
  });
  typia.assert(byOwner);
  TestValidator.predicate("All results have correct owner_id")(() => byOwner.data.every(m => m.owner_id === createdModel.owner_id));
  TestValidator.predicate("Result includes created model by owner")(() => byOwner.data.some(m => m.id === createdModel.id));

  // 5. Filter: By name (exact match)
  const byName: IPageIAiModel = await api.functional.advancedAI.aiModels.patch(connection, {
    body: { name: createdModel.name }
  });
  typia.assert(byName);
  TestValidator.predicate("All results have correct name")(() => byName.data.every(m => m.name === createdModel.name));
  TestValidator.predicate("Result includes created model by name")(() => byName.data.some(m => m.id === createdModel.id));

  // 6. Filter: Nonexisting model_type should return empty results
  const emptyType: IPageIAiModel = await api.functional.advancedAI.aiModels.patch(connection, {
    body: { model_type: "no-such-type-xyz-${Math.random()}" }
  });
  typia.assert(emptyType);
  TestValidator.equals("Empty result by nonexisting type")([] as IAiModel[])(emptyType.data);

  // 7. Filter: Nonexisting name should return empty results
  const emptyName: IPageIAiModel = await api.functional.advancedAI.aiModels.patch(connection, {
    body: { name: "no-model-${Math.random()}" }
  });
  typia.assert(emptyName);
  TestValidator.equals("Empty result by nonexisting name")([] as IAiModel[])(emptyName.data);

  // 8. Confirm pagination object is present and consistent
  const checkPages = (result: IPageIAiModel) => {
    TestValidator.predicate("pagination object present")(() => !!result.pagination && typeof result.pagination.current === "number");
    TestValidator.predicate("pagination records >= data.length")(() => result.pagination.records >= result.data.length);
    TestValidator.predicate("pagination pages >= 1")(() => result.pagination.pages >= 1 || result.pagination.pages === 0);
    TestValidator.predicate("pagination limit > 0")(() => result.pagination.limit > 0);
  };
  checkPages(allList);
  checkPages(byType);
  checkPages(byOwner);
  checkPages(byName);
  checkPages(emptyType);
  checkPages(emptyName);

  // 9. Check deterministic ordering when using pagination (first page)
  const paged1: IPageIAiModel = await api.functional.advancedAI.aiModels.patch(connection, { body: { limit: 2, page: 1 } });
  const paged2: IPageIAiModel = await api.functional.advancedAI.aiModels.patch(connection, { body: { limit: 2, page: 2 } });
  typia.assert(paged1);
  typia.assert(paged2);
  TestValidator.predicate("No data overlap between pages")(
    () => paged1.data.every(a => paged2.data.every(b => b.id !== a.id)) || paged2.data.length === 0 || paged1.data.length === 0
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IAiModel } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiModel";
