export async function test_get_ai_model_by_id_with_valid_and_invalid_cases(connection: api.IConnection): Promise<void> {
  // 1. Create a new AI model for testing (positive case)
  const createInput: IAiModel.ICreate = {
    organization_id: typia.random<string & tags.Format<"uuid">>(),
    owner_id: typia.random<string & tags.Format<"uuid">>(),
    name: `test-model-${Date.now()}`,
    model_type: "classification",
    description: "E2E test - model fetch by id"
  };

  const created: IAiModel = await api.functional.advancedAI.aiModels.post(connection, { body: createInput });
  typia.assert<IAiModel>(created);

  // 2. Fetch model by its valid ID
  const fetched: IAiModel = await api.functional.advancedAI.aiModels.getById(connection, { id: created.id });
  typia.assert<IAiModel>(fetched);
  // 3. Validate key fields are correctly returned
