export async function test_create_ai_model_with_valid_invalid_and_duplicate_data(
  connection: api.IConnection
): Promise<void> {
  // 1. Generate valid AI model data
  const validModel: IAiModel.ICreate = {
    organization_id: typia.random<"uuid">(),
    owner_id: typia.random<"uuid">(),
    name: `ai-model-${Math.random().toString(16).slice(2,8)}`,
    model_type: "classification",
    description: "Test AI model for E2E test.",
  };

  // 2. Create the model
  const created = await api.functional.advancedAI.aiModels.post(connection, {
    body: validModel,
  });
  typia.assert<IAiModel>(created);
  TestValidator.equals("Returned fields must match input")<Partial<IAiModel.ICreate>>({
    organization_id: validModel.organization_id,
    owner_id: validModel.owner_id,
    name: validModel.name,
    model_type: validModel.model_type,
    description: validModel.description,
  })(created);

  // 3. Attempt creation with missing required field (missing name)
  await TestValidator.httpError("Missing name must raise error")(400)(async () => {
