  const createInput: IAiModel.ICreate = {
    organization_id: typia.random<string & tags.Format<"uuid">>(),
    owner_id: typia.random<string & tags.Format<"uuid">>(),
    name: `test-model-${Math.random().toString(36).substring(2, 8)}`,
    model_type: "classification",
    description: "Initial test model for update checks.",
  };
  const created: IAiModel = await api.functional.advancedAI.aiModels.post(
    connection,
    { body: createInput },
  );
  typia.assert<IAiModel>(created);

  // 2. Update metadata (valid): description, owner_id, model_type
  const updateInput: IAiModel.IUpdate = {
    description: "Updated by test.",
    owner_id: typia.random<string & tags.Format<"uuid">>(),
    model_type: "recommendation",
  };
  const updated: IAiModel = await api.functional.advancedAI.aiModels.putById(
    connection,
    { id: created.id, body: updateInput },
  );
  typia.assert<IAiModel>(updated);
  TestValidator.equals("updated id matches")(created.id)(updated.id);
  TestValidator.equals("description updated")(<string>updateInput.description)(updated.description!);
  TestValidator.equals("model_type updated")(<string>updateInput.model_type)(updated.model_type);
  TestValidator.equals("owner_id updated")(<string>updateInput.owner_id)(updated.owner_id);

  // 3. Try update with invalid/nonexistent id
  await TestValidator.httpError("PUT with invalid id should fail")(404, 400)(async () => {
    await api.functional.advancedAI.aiModels.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(), // random UUID, unlikely to exist
      body: updateInput,
    });
  });

  // 4. Attempt update with invalid owner_id (malformed UUID)
  await TestValidator.httpError("PUT with invalid owner_id should fail")(400, 422)(async () => {
    await api.functional.advancedAI.aiModels.putById(connection, {
      id: created.id,
      body: { owner_id: "INVALID-UUID" as any },
    });
  });

  // 5. Attempt update with empty body (no fields), should be allowed but should make no changes or trigger a validation error
  await TestValidator.httpError("PUT with empty object")(400, 422)(async () => {
    await api.functional.advancedAI.aiModels.putById(connection, {
      id: created.id,
      body: {},
    });
  });
