import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IAiModelVersion } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiModelVersion";
import { IPageIAiModelVersion } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiModelVersion";

export async function test_list_and_search_ai_model_versions_with_pagination_and_filters(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create two model versions under different parent model ids
  const parentModelId1 = typia.random<typeof IAiModelVersion>()?.ai_model_id ??
    "11111111-1111-1111-1111-111111111111";
  const parentModelId2 = typia.random<typeof IAiModelVersion>()?.ai_model_id ??
    "22222222-2222-2222-2222-222222222222";

  const createInput1: IAiModelVersion.ICreate = {
    ai_model_id: parentModelId1,
    version: "test-v1.0.0",
    version_hash: "hash-abc-123",
    artifact_url: "s3://bucket/model1/v1.0.0/model.pt",
    framework: "PyTorch",
    metadata: '{"accuracy":0.97}',
  };
  const createInput2: IAiModelVersion.ICreate = {
    ai_model_id: parentModelId2,
    version: "prod-v2.5.1",
    version_hash: "hash-def-456",
    artifact_url: "s3://bucket/model2/v2.5.1/model.pb",
    framework: "TensorFlow",
    metadata: '{"f1":0.89}',
  };
  const created1 = await api.functional.advancedAI.aiModelVersions.post(connection, { body: createInput1 });
  const created2 = await api.functional.advancedAI.aiModelVersions.post(connection, { body: createInput2 });
  typia.assert(created1);
  typia.assert(created2);

  // Step 2a: List all model versions (without filters)
  const listAllRes = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: {},
  });
  typia.assert<IPageIAiModelVersion>(listAllRes);
  // Confirm both entities are present
  TestValidator.predicate("created1 in listAll")(() =>
    listAllRes.data.some(mv => mv.id === created1.id),
  );
  TestValidator.predicate("created2 in listAll")(() =>
    listAllRes.data.some(mv => mv.id === created2.id),
  );

  // Step 2b: Filter by ai_model_id
  const filterByModel1 = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { ai_model_id: parentModelId1 },
  });
  typia.assert<IPageIAiModelVersion>(filterByModel1);
  for (const mv of filterByModel1.data) {
    TestValidator.equals("ai_model_id matches", undefined)(mv.ai_model_id)(parentModelId1);
  }
  TestValidator.predicate("created1 found by model filter")(() =>
    filterByModel1.data.some(mv => mv.id === created1.id),
  );
  TestValidator.predicate("created2 not in model1 filter")(() =>
    !filterByModel1.data.some(mv => mv.id === created2.id),
  );

  // Step 2c: Filter by version (partial and full)
  const filterByVersionFull = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { version: "test-v1.0.0" },
  });
  typia.assert<IPageIAiModelVersion>(filterByVersionFull);
  for (const mv of filterByVersionFull.data) {
    TestValidator.equals("version matches", undefined)(mv.version)("test-v1.0.0");
  }
  TestValidator.predicate("created1 found by full version")(() =>
    filterByVersionFull.data.some(mv => mv.id === created1.id),
  );

  const filterByVersionPartial = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { version: "prod-v2" },
  });
  typia.assert<IPageIAiModelVersion>(filterByVersionPartial);
  TestValidator.predicate("created2 found by partial version")(() =>
    filterByVersionPartial.data.some(mv => mv.id === created2.id),
  );

  // Step 2d: Filter by framework
  const filterByFramework = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { framework: "TensorFlow" },
  });
  typia.assert<IPageIAiModelVersion>(filterByFramework);
  for (const mv of filterByFramework.data) {
    TestValidator.equals("framework matches", undefined)(mv.framework)("TensorFlow");
  }
  TestValidator.predicate("created2 found by framework")(() =>
    filterByFramework.data.some(mv => mv.id === created2.id),
  );

  // Step 2e: Combined filters ai_model_id + version
  const comboFilters = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { ai_model_id: parentModelId2, version: "prod-v2.5.1" },
  });
  typia.assert<IPageIAiModelVersion>(comboFilters);
  TestValidator.predicate("created2 found by combo filters")(() =>
    comboFilters.data.some(mv => mv.id === created2.id),
  );
  for (const mv of comboFilters.data) {
    TestValidator.equals("ai_model_id matches combo", undefined)(mv.ai_model_id)(parentModelId2);
    TestValidator.equals("version matches combo", undefined)(mv.version)("prod-v2.5.1");
  }

  // Step 4: Empty result filtering (non-existent combo)
  const emptyRes = await api.functional.advancedAI.aiModelVersions.patch(connection, {
    body: { ai_model_id: "00000000-0000-0000-0000-000000000000", version: "does-not-exist" },
  });
  typia.assert<IPageIAiModelVersion>(emptyRes);
  TestValidator.equals("no data for fake filters")(emptyRes.data)([] as IAiModelVersion[]);
}

