import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IAiModel } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiModel";
import { IAiModelVersion } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiModelVersion";

export async function test_create_ai_model_version_with_parent_model_and_invalid_data(
  connection: api.IConnection
): Promise<void> {
  // 1. Create parent AI Model
  const parentModelInput: IAiModel.ICreate = {
    organization_id: typia.random<string & typia.tags.Format<"uuid">>(),
    owner_id: typia.random<string & typia.tags.Format<"uuid">>(),
    name: "test-model-" + Math.random().toString(36).substring(2, 8),
    model_type: "classification",
    description: "Test model for AI version linkage.",
  };
  const parentModel: IAiModel = await api.functional.advancedAI.aiModels.post(
    connection,
    { body: parentModelInput }
  );
  typia.assert<IAiModel>(parentModel);
  TestValidator.equals("parent model name")(parentModelInput.name)(parentModel.name);
  TestValidator.equals("parent model type")(parentModelInput.model_type)(parentModel.model_type);

  // 2. Create AI Model Version with proper linkage to the parent
  const modelVersionInput: IAiModelVersion.ICreate = {
    ai_model_id: parentModel.id,
    version: "v1.0.0",
    version_hash: "hash-" + Math.random().toString(36).substring(2,10),
    artifact_url: "s3://bucket/path/to/artifact",
    framework: "PyTorch",
    metadata: '{"test":true}',
  };
  const createdVersion: IAiModelVersion = await api.functional.advancedAI.aiModelVersions.post(
    connection,
    { body: modelVersionInput }
  );
  typia.assert<IAiModelVersion>(createdVersion);
  TestValidator.equals("version linkage to parent")(parentModel.id)(createdVersion.ai_model_id);
  TestValidator.equals("version string")(modelVersionInput.version)(createdVersion.version);
  TestValidator.equals("framework matches")(modelVersionInput.framework)(createdVersion.framework);
  TestValidator.equals("artifact_url matches")(modelVersionInput.artifact_url)(createdVersion.artifact_url);

  // 3. Attempt model version creation: missing required field 'version_hash'
  const invalidInputMissingHash: IAiModelVersion.ICreate = {
    ...modelVersionInput,
  };
  delete (invalidInputMissingHash as any).version_hash; // Remove required field
  await TestValidator.httpError("missing required field 'version_hash'")(400)(async () => {
    // Will throw validation error
    return api.functional.advancedAI.aiModelVersions.post(connection, { body: invalidInputMissingHash as any });
  });

  // 4. Attempt model version creation: ai_model_id is empty string
  const invalidInputMissingParent: IAiModelVersion.ICreate = {
    ...modelVersionInput,
    ai_model_id: "" as any,
  };
  await TestValidator.httpError("empty string for ai_model_id")(400)(async () => {
    return api.functional.advancedAI.aiModelVersions.post(connection, { body: invalidInputMissingParent });
  });

  // 5. Attempt creation with version_hash wrong type (number, should fail)
  const invalidInputBadType: any = {
    ...modelVersionInput,
    version_hash: 1234567890
  };
  await TestValidator.httpError("wrong type for version_hash")(400, 422)(async () => {
    return api.functional.advancedAI.aiModelVersions.post(connection, { body: invalidInputBadType });
  });

  // No explicit audit trail in return type, but creation and error tests act as audit checks indirectly.
}

