import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IAITrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IAITrainingData";
import { IAiTrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiTrainingData";
import { IPageIAiTrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiTrainingData";
import typia from "typia";

/**
 * E2E test: Search AI Training Data with pagination and various filters.
 */
export async function test_search_ai_training_data_with_pagination_and_various_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Prepare: Create 3 datasets with distinct names and properties
  const now = new Date();
  const ai_model_id1 = typia.random<string>();
  const ai_model_id2 = typia.random<string>();

  const datasetA = await api.functional.advancedAI.aiTrainingData.post(connection, {
    body: {
      name: "Finance2024Q1",
      version: "v1",
      source_uri: "s3://bucket/finance-2024-q1.csv",
      schema_hash: "hash-alpha",
      ai_model_id: ai_model_id1,
      record_count: 1000,
    },
  });
  const datasetB = await api.functional.advancedAI.aiTrainingData.post(connection, {
    body: {
      name: "Marketing2024Q1",
      version: "v2",
      source_uri: "s3://bucket/marketing-2024-q1.csv",
      schema_hash: "hash-beta",
      ai_model_id: ai_model_id2,
      record_count: 2000,
    },
  });
  const datasetC = await api.functional.advancedAI.aiTrainingData.post(connection, {
    body: {
      name: "Finance2024Q1",
      version: "v2",
      source_uri: "s3://bucket/finance-2024-q1-revised.csv",
      schema_hash: "hash-gamma",
      ai_model_id: ai_model_id1,
      record_count: 2500,
    },
  });

  // 2. Search by exact name
  {
    const output = await api.functional.advancedAI.aiTrainingData.patch(connection, {
      body: { name: "Finance2024Q1" },
    });
    typia.assert<IPageIAiTrainingData>(output);
    TestValidator.predicate("all have correct name")(
      () => output.data.every(d => d.name === "Finance2024Q1")
    );
    TestValidator.predicate("at least 2 results")(
      () => output.data.length >= 2
    );
  }

  // 3. Search by partial name (e.g., contains 'Marketing')
  {
    const output = await api.functional.advancedAI.aiTrainingData.patch(connection, {
      body: { name: "Marketing" },
    });
    typia.assert<IPageIAiTrainingData>(output);
    TestValidator.predicate("all contain 'Marketing' in name")(
      () => output.data.every(d => d.name.includes("Marketing"))
    );
    TestValidator.predicate("should include Marketing2024Q1")(
      () => output.data.some(d => d.id === datasetB.id)
    );
  }

  // 4. Search with ai_model_id
  {
    const output = await api.functional.advancedAI.aiTrainingData.patch(connection, {
      body: { ai_model_id: ai_model_id1 },
    });
    typia.assert<IPageIAiTrainingData>(output);
    TestValidator.predicate("all with ai_model_id1")(
      () => output.data.every(d => d.ai_model_id === ai_model_id1)
    );
    TestValidator.predicate("has both datasetA and datasetC")(
      () => output.data.some(d => d.id === datasetA.id) &&
            output.data.some(d => d.id === datasetC.id)
    );
  }

  // 5. Search by record_count range
  {
    const output = await api.functional.advancedAI.aiTrainingData.patch(connection, {
      body: { record_count_from: 1800, record_count_to: 3000 },
    });
    typia.assert<IPageIAiTrainingData>(output);
    TestValidator.predicate("all within record_count range")(
      () => output.data.every(d => d.record_count !== null && d.record_count !== undefined && d.record_count >= 1800 && d.record_count <= 3000)
    );
    TestValidator.predicate("contains datasetB and C")(
      () => output.data.some(d => d.id === datasetB.id) &&
            output.data.some(d => d.id === datasetC.id)
    );
  }

  // 6. Search by schema_hash
  {
    const output = await api.functional.advancedAI.aiTrainingData.patch(connection, {
      body: { schema_hash: "hash-beta" },
    });
    typia.assert<IPageIAiTrainingData>(output);
    TestValidator.predicate("all have schema_hash beta")(
      () => output.data.every(d => d.schema_hash === "hash-beta")
    );
    TestValidator.predicate("contains datasetB")(
      () => output.data.some(d => d.id === datasetB.id)
    );
  }

  // 7. Search by created_from and created_to
  {
    // Use datasetA's created_at as a reference
    const created_from = datasetA.created_at;
    const output = await api.functional.advancedAI.aiTrainingData.patch(connection, {
      body: { created_from },
    });
    typia.assert<IPageIAiTrainingData>(output);
    TestValidator.predicate("all created_at >= created_from")(
      () => output.data.every(d => new Date(d.created_at) >= new Date(created_from))
    );
  }

  // 8. Pagination: limit 2 per page, test multiple pages
  {
    const outputPage1 = await api.functional.advancedAI.aiTrainingData.patch(connection, {
      body: { limit: 2, page: 1 },
    });
    typia.assert<IPageIAiTrainingData>(outputPage1);
    TestValidator.equals("limit 2 per page")(
      2
    )(outputPage1.pagination.limit);
    // The number of returned data may be less than 2 if there are not enough datasets
    TestValidator.predicate("<= 2 items returned")(
      () => outputPage1.data.length <= 2
    );
    if (outputPage1.pagination.pages > 1) {
      const outputPage2 = await api.functional.advancedAI.aiTrainingData.patch(connection, {
        body: { limit: 2, page: 2 },
      });
      typia.assert<IPageIAiTrainingData>(outputPage2);
      TestValidator.equals("limit still 2 per page")(
        2
      )(outputPage2.pagination.limit);
      TestValidator.predicate("different result page")(
        () =>
          outputPage2.data.length > 0 &&
          outputPage2.data.every(d =>
            outputPage1.data.every(d1 => d1.id !== d.id),
          )
      );
