import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IPageIPolicy } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPolicy";
import { IPolicy } from "@ORGANIZATION/PROJECT-api/lib/structures/IPolicy";

export async function test_list_permission_policies_with_filter_and_pagination(
  connection: api.IConnection
): Promise<void> {
  // 1. Query first page with limit (test pagination result fields and type integrity)
  const page1 = await api.functional.securityCompliance.policy.patch(connection, {
    body: { page: 1, limit: 2 },
  });
  typia.assert<IPageIPolicy>(page1);
  TestValidator.predicate("pagination page1 items")(page1.data.length <= 2);
  TestValidator.predicate("pagination page1 meta")(
    () => page1.pagination.current === 1 && page1.pagination.limit === 2
  );

  // store a sample name if policies exist
  const sampledName = page1.data[0]?.name ?? undefined;

  // 2. Query with partial name filter (should return at least one policy if sampledName exists)
  if (sampledName) {
    const namePart = sampledName.slice(0, Math.max(1, Math.floor(sampledName.length / 2)));
    const filtered = await api.functional.securityCompliance.policy.patch(connection, {
      body: { name: namePart },
    });
    typia.assert<IPageIPolicy>(filtered);
    TestValidator.predicate("name filter contains sampled")(filtered.data.some(p => p.name.includes(namePart)));
  }

  // 3. Query with restrictive filter (should yield empty data array)
  const noneResult = await api.functional.securityCompliance.policy.patch(connection, {
    body: { name: "definitely_nonexistent_policy_name_zzzz" },
  });
  typia.assert<IPageIPolicy>(noneResult);
  TestValidator.equals("empty by restrictive filter")([] as IPolicy[])(noneResult.data);

  // 4. Edge: no results for created_from > created_to
  if (page1.data.length > 0) {
    const cFrom = page1.data[0].created_at;
    const cTo = page1.data[0].created_at;
    const reversed = await api.functional.securityCompliance.policy.patch(connection, {
      body: { created_from: cTo, created_to: cFrom },
    });
    typia.assert<IPageIPolicy>(reversed);
    TestValidator.equals("created_from after created_to yields empty")([] as IPolicy[])(reversed.data);
  }

  // 5. Invalid filter param: negative page
  await TestValidator.httpError("invalid page param")(400)(async () => {
    await api.functional.securityCompliance.policy.patch(connection, {
      body: { page: -1 },
    });
  });

  // 6. Invalid filter param: malformed date
  await TestValidator.httpError("invalid date param")(400)(async () => {
    await api.functional.securityCompliance.policy.patch(connection, {
      body: { created_from: "not-a-date" },
    });
  });
}

